import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { quizAPI } from '../services/api';

const QuizPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  
  const [quiz, setQuiz] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [quizStarted, setQuizStarted] = useState(false);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');

  const fetchQuizData = useCallback(async () => {
    try {
      const [quizResponse, questionsResponse] = await Promise.all([
        quizAPI.getQuiz(id),
        quizAPI.getQuizQuestions(id)
      ]);
      
      setQuiz(quizResponse.data);
      setQuestions(questionsResponse.data);
      setTimeLeft(quizResponse.data.durationMinutes * 60);
    } catch (error) {
      setError('Error loading quiz');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  }, [id]);

  const handleSubmitQuiz = useCallback(async () => {
    if (submitting) return;
    
    setSubmitting(true);
    const startTime = quiz.durationMinutes * 60;
    const timeTaken = startTime - timeLeft;

    try {
      const response = await quizAPI.submitQuiz({
        quizId: parseInt(id),
        answers,
        timeTakenSeconds: timeTaken
      });
      
      navigate(`/result/${response.data.id}`);
    } catch (error) {
      setError('Error submitting quiz');
      console.error('Error:', error);
      setSubmitting(false);
    }
  }, [submitting, quiz, timeLeft, id, answers, navigate]);

  useEffect(() => {
    fetchQuizData();
  }, [fetchQuizData]);

  useEffect(() => {
    let timer;
    if (quizStarted && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            handleSubmitQuiz();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [quizStarted, timeLeft, handleSubmitQuiz]);

  const startQuiz = () => {
    setQuizStarted(true);
  };

  const handleAnswerSelect = (questionId, answer) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: answer
    }));
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const getTimeColor = () => {
    if (timeLeft <= 60) return 'timer-danger';
    if (timeLeft <= 300) return 'timer-warning';
    return 'timer-safe';
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto mt-8 px-4">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg" role="alert">
          <div className="flex items-center">
            <i className="fas fa-exclamation-triangle mr-2"></i>
            <span>{error}</span>
          </div>
        </div>
      </div>
    );
  }

  // Quiz Start Screen
  if (!quizStarted) {
    return (
      <div className="container mx-auto mt-8 px-4">
        <div className="flex justify-center">
          <div className="w-full max-w-4xl">
            <div className="quiz-card">
              <div className="text-center p-12">
                <i className="fas fa-brain text-6xl text-primary-500 mb-6"></i>
                <h1 className="text-4xl font-bold text-gray-800 mb-4">{quiz?.title}</h1>
                <p className="text-xl text-gray-600 mb-8">{quiz?.description}</p>
                
                <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-8">
                  <div className="flex flex-col items-center">
                    <i className="fas fa-clock text-4xl text-blue-500 mb-4"></i>
                    <h5 className="text-lg font-semibold text-gray-700 mb-2">Duration</h5>
                    <p className="text-gray-600">{quiz?.durationMinutes} minutes</p>
                  </div>
                  <div className="flex flex-col items-center">
                    <i className="fas fa-question-circle text-4xl text-green-500 mb-4"></i>
                    <h5 className="text-lg font-semibold text-gray-700 mb-2">Questions</h5>
                    <p className="text-gray-600">{questions.length} questions</p>
                  </div>
                  <div className="flex flex-col items-center">
                    <i className="fas fa-star text-4xl text-yellow-500 mb-4"></i>
                    <h5 className="text-lg font-semibold text-gray-700 mb-2">Total Marks</h5>
                    <p className="text-gray-600">{quiz?.totalMarks || questions.length}</p>
                  </div>
                </div>

                <div className="bg-blue-50 border border-blue-200 rounded-lg p-6 mb-8">
                  <h6 className="flex items-center text-lg font-semibold text-blue-800 mb-4">
                    <i className="fas fa-info-circle mr-2"></i>
                    Instructions:
                  </h6>
                  <ul className="text-left text-blue-700 space-y-2">
                    <li className="flex items-start">
                      <span className="mr-2">•</span>
                      <span>Read each question carefully</span>
                    </li>
                    <li className="flex items-start">
                      <span className="mr-2">•</span>
                      <span>Select the best answer for each question</span>
                    </li>
                    <li className="flex items-start">
                      <span className="mr-2">•</span>
                      <span>You can navigate between questions</span>
                    </li>
                    <li className="flex items-start">
                      <span className="mr-2">•</span>
                      <span>The quiz will auto-submit when time is up</span>
                    </li>
                    <li className="flex items-start">
                      <span className="mr-2">•</span>
                      <span>Make sure you have a stable internet connection</span>
                    </li>
                  </ul>
                </div>

                <button 
                  className="btn-primary text-lg px-8 py-4 inline-flex items-center"
                  onClick={startQuiz}
                >
                  <i className="fas fa-play mr-3"></i>
                  Start Quiz
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="container mx-auto mt-6 px-4">
      {/* Timer and Progress */}
      <div className="mb-6">
        <div className="quiz-card">
          <div className="py-4 px-6">
            <div className="flex justify-between items-center">
              <div>
                <h6 className="text-lg font-semibold text-gray-800 mb-1">{quiz?.title}</h6>
                <small className="text-gray-500">
                  Question {currentQuestionIndex + 1} of {questions.length}
                </small>
              </div>
              <div className="text-center">
                <div className="w-48 bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-primary-500 h-2 rounded-full transition-all duration-300" 
                    style={{ width: `${((currentQuestionIndex + 1) / questions.length) * 100}%` }}
                  ></div>
                </div>
              </div>
              <div className="text-right">
                <h4 className={`text-2xl font-bold ${getTimeColor()}`}>
                  <i className="fas fa-clock mr-2"></i>
                  {formatTime(timeLeft)}
                </h4>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        {/* Question */}
        <div className="lg:col-span-3">
          <div className="quiz-card mb-6">
            <div className="p-8">
              <h5 className="text-xl font-semibold text-gray-800 mb-6">
                Question {currentQuestionIndex + 1}
              </h5>
              <p className="text-lg text-gray-700 mb-8 leading-relaxed">{currentQuestion?.questionText}</p>
              
              <div className="space-y-4">
                {['A', 'B', 'C', 'D'].map((option) => (
                  <div 
                    key={option}
                    className={`option-card ${answers[currentQuestion?.id] === option ? 'selected' : ''}`}
                    onClick={() => handleAnswerSelect(currentQuestion?.id, option)}
                  >
                    <div className="flex items-center">
                      <input
                        className="form-radio h-5 w-5 text-primary-500 mr-4"
                        type="radio"
                        name={`question-${currentQuestion?.id}`}
                        id={`${currentQuestion?.id}-${option}`}
                        checked={answers[currentQuestion?.id] === option}
                        onChange={() => handleAnswerSelect(currentQuestion?.id, option)}
                      />
                      <label 
                        className="flex-1 cursor-pointer text-gray-700" 
                        htmlFor={`${currentQuestion?.id}-${option}`}
                      >
                        <span className="font-semibold mr-3">{option})</span>
                        <span>{currentQuestion?.[`option${option}`]}</span>
                      </label>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Navigation */}
          <div className="quiz-card">
            <div className="p-6">
              <div className="flex justify-between items-center">
                <button
                  className="btn-outline inline-flex items-center"
                  onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))}
                  disabled={currentQuestionIndex === 0}
                >
                  <i className="fas fa-chevron-left mr-2"></i>
                  Previous
                </button>
                
                <div className="flex space-x-4">
                  {currentQuestionIndex === questions.length - 1 ? (
                    <button
                      className="btn-success inline-flex items-center"
                      onClick={handleSubmitQuiz}
                      disabled={submitting}
                    >
                      {submitting ? (
                        <>
                          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                          Submitting...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-check mr-2"></i>
                          Submit Quiz
                        </>
                      )}
                    </button>
                  ) : (
                    <button
                      className="btn-primary inline-flex items-center"
                      onClick={() => setCurrentQuestionIndex(Math.min(questions.length - 1, currentQuestionIndex + 1))}
                    >
                      Next
                      <i className="fas fa-chevron-right ml-2"></i>
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Question Navigator */}
        <div className="lg:col-span-1">
          <div className="quiz-card sticky top-6">
            <div className="bg-gradient-to-r from-primary-500 to-primary-600 text-white px-6 py-4 rounded-t-xl">
              <h6 className="text-lg font-semibold">Question Navigator</h6>
            </div>
            <div className="p-6">
              <div className="grid grid-cols-5 lg:grid-cols-4 gap-2 mb-6">
                {questions.map((question, index) => (
                  <button
                    key={question.id}
                    className={`question-nav-btn ${
                      index === currentQuestionIndex 
                        ? 'current' 
                        : answers[question.id] 
                        ? 'answered' 
                        : 'unanswered'
                    }`}
                    onClick={() => setCurrentQuestionIndex(index)}
                  >
                    {index + 1}
                  </button>
                ))}
              </div>
              
              <div className="text-sm text-gray-600 space-y-3">
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-success-500 rounded-full mr-3"></div>
                  <span>Answered ({Object.keys(answers).length})</span>
                </div>
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-primary-500 rounded-full mr-3"></div>
                  <span>Current</span>
                </div>
                <div className="flex items-center">
                  <div className="w-4 h-4 bg-gray-200 rounded-full mr-3"></div>
                  <span>Not Answered ({questions.length - Object.keys(answers).length})</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuizPage;
