import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { quizAPI } from '../services/api';

const ResultPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  
  const [result, setResult] = useState(null);
  const [quiz, setQuiz] = useState(null);
  const [userAnswers] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showDetails, setShowDetails] = useState(false);

  const fetchResultData = useCallback(async () => {
    try {
      const resultResponse = await quizAPI.getResult(id);
      const result = resultResponse.data;
      
      setResult({
        score: result.score,
        correctAnswers: result.correctAnswers,
        incorrectAnswers: result.wrongAnswers,
        unanswered: result.totalQuestions - result.correctAnswers - result.wrongAnswers,
        timeTakenSeconds: result.timeTakenSeconds,
        submittedAt: result.completedAt
      });
      
      setQuiz({
        title: result.quizTitle,
        totalMarks: result.score + (result.wrongAnswers * 1) // Assuming 1 mark per question
      });
      
      // Convert answer reviews to questions format
      const questionsFromReviews = result.answerReviews.map(review => ({
        id: review.questionId,
        questionText: review.questionText,
        optionA: review.optionA,
        optionB: review.optionB,
        optionC: review.optionC,
        optionD: review.optionD,
        correctAnswer: review.correctAnswer,
        marks: 1
      }));
      
      setQuestions(questionsFromReviews);
      
      // Convert to user answers format
      const userAnswersFromReviews = result.answerReviews
        .filter(review => review.selectedAnswer)
        .map(review => ({
          questionId: review.questionId,
          selectedOption: review.selectedAnswer
        }));
      
      setUserAnswers(userAnswersFromReviews);
      
    } catch (error) {
      setError('Error loading results');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchResultData();
  }, [fetchResultData]);

  const getScorePercentage = () => {
    if (!result || !quiz) return 0;
    return Math.round((result.score / quiz.totalMarks) * 100);
  };

  const getScoreColor = () => {
    const percentage = getScorePercentage();
    if (percentage >= 80) return 'success';
    if (percentage >= 60) return 'warning';
    return 'danger';
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}m ${remainingSeconds}s`;
  };

  const getPerformanceMessage = () => {
    const percentage = getScorePercentage();
    if (percentage >= 90) return { message: "Excellent! Outstanding performance!", icon: "fas fa-trophy", color: "text-warning" };
    if (percentage >= 80) return { message: "Great job! Very good performance!", icon: "fas fa-star", color: "text-success" };
    if (percentage >= 70) return { message: "Good work! Above average performance!", icon: "fas fa-thumbs-up", color: "text-info" };
    if (percentage >= 60) return { message: "Fair performance. Keep practicing!", icon: "fas fa-hand-paper", color: "text-warning" };
    return { message: "Need improvement. Don't give up!", icon: "fas fa-heart", color: "text-danger" };
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto mt-8 px-4">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg" role="alert">
          <div className="flex items-center">
            <i className="fas fa-exclamation-triangle mr-2"></i>
            <span>{error}</span>
          </div>
        </div>
      </div>
    );
  }

  const performance = getPerformanceMessage();

  return (
    <div className="container mx-auto mt-8 px-4">
      <div className="flex justify-center">
        <div className="w-full max-w-6xl">
          {/* Result Header */}
          <div className="quiz-card bg-gradient-to-r from-purple-600 to-blue-600 text-white mb-8">
            <div className="text-center p-12">
              <i className={`${performance.icon} text-6xl mb-6`}></i>
              <h1 className="text-4xl font-bold mb-4">Quiz Completed!</h1>
              <h2 className="text-2xl font-light mb-6">{quiz?.title}</h2>
              <p className="text-xl font-medium">
                {performance.message}
              </p>
            </div>
          </div>

          {/* Score Card */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
            <div className="quiz-card">
              <div className="text-center p-8">
                <div className="relative inline-flex items-center justify-center w-32 h-32 mb-6">
                  <svg className="w-32 h-32 transform -rotate-90">
                    <circle
                      cx="64"
                      cy="64"
                      r="56"
                      stroke="currentColor"
                      strokeWidth="8"
                      fill="transparent"
                      className="text-gray-200"
                    />
                    <circle
                      cx="64"
                      cy="64"
                      r="56"
                      stroke="currentColor"
                      strokeWidth="8"
                      fill="transparent"
                      strokeDasharray={`${2 * Math.PI * 56}`}
                      strokeDashoffset={`${2 * Math.PI * 56 * (1 - getScorePercentage() / 100)}`}
                      className={`${
                        getScoreColor() === 'success' ? 'text-green-500' :
                        getScoreColor() === 'warning' ? 'text-yellow-500' : 'text-red-500'
                      } transition-all duration-1000 ease-out`}
                      strokeLinecap="round"
                    />
                  </svg>
                  <div className="absolute inset-0 flex flex-col items-center justify-center">
                    <span className="text-3xl font-bold text-gray-800">{getScorePercentage()}%</span>
                    <span className="text-sm text-gray-500">Score</span>
                  </div>
                </div>
                <h4 className="text-2xl font-bold text-gray-800">
                  {result?.score || 0} / {quiz?.totalMarks || questions.length}
                </h4>
                <p className="text-gray-500">Total Marks</p>
              </div>
            </div>
            
            <div className="quiz-card">
              <div className="p-8">
                <h5 className="text-xl font-bold text-gray-800 mb-6 flex items-center">
                  <i className="fas fa-chart-bar mr-3"></i>
                  Performance Summary
                </h5>
                
                <div className="grid grid-cols-3 gap-4 text-center mb-6">
                  <div>
                    <h3 className="text-3xl font-bold text-green-500">{result?.correctAnswers || 0}</h3>
                    <p className="text-gray-500 text-sm">Correct</p>
                  </div>
                  <div>
                    <h3 className="text-3xl font-bold text-red-500">{result?.incorrectAnswers || 0}</h3>
                    <p className="text-gray-500 text-sm">Incorrect</p>
                  </div>
                  <div>
                    <h3 className="text-3xl font-bold text-yellow-500">{result?.unanswered || 0}</h3>
                    <p className="text-gray-500 text-sm">Skipped</p>
                  </div>
                </div>
                
                <div className="border-t pt-6 space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="flex items-center text-gray-600">
                      <i className="fas fa-clock mr-2"></i>Time Taken:
                    </span>
                    <span className="font-semibold">{formatTime(result?.timeTakenSeconds || 0)}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="flex items-center text-gray-600">
                      <i className="fas fa-calendar mr-2"></i>Completed:
                    </span>
                    <span className="font-semibold">{new Date(result?.submittedAt).toLocaleString()}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="text-center mb-8">
            <button
              className="btn-primary mr-4 inline-flex items-center"
              onClick={() => setShowDetails(!showDetails)}
            >
              <i className="fas fa-eye mr-2"></i>
              {showDetails ? 'Hide' : 'View'} Detailed Results
            </button>
            
            <button
              className="btn-outline mr-4 inline-flex items-center"
              onClick={() => navigate('/dashboard')}
            >
              <i className="fas fa-home mr-2"></i>
              Back to Dashboard
            </button>
            
            <button
              className="btn-success inline-flex items-center"
              onClick={() => navigate('/quizzes')}
            >
              <i className="fas fa-redo mr-2"></i>
              Take Another Quiz
            </button>
          </div>

          {/* Detailed Results */}
          {showDetails && (
            <div className="quiz-card">
              <div className="bg-gradient-to-r from-primary-500 to-primary-600 text-white px-6 py-4 rounded-t-xl">
                <h5 className="text-xl font-semibold flex items-center">
                  <i className="fas fa-list-alt mr-2"></i>
                  Detailed Results
                </h5>
              </div>
              <div className="p-6">
                {questions.map((question, index) => {
                  const userAnswer = userAnswers.find(ua => ua.questionId === question.id);
                  const isCorrect = userAnswer?.selectedOption === question.correctAnswer;
                  const wasAnswered = userAnswer?.selectedOption;
                  
                  return (
                    <div key={question.id} className="mb-8 p-6 bg-gray-50 rounded-lg">
                      <div className="flex items-start">
                        <div className="flex-shrink-0 mr-4">
                          <div className={`w-10 h-10 rounded-full flex items-center justify-center text-white font-bold ${
                            isCorrect ? 'bg-green-500' : wasAnswered ? 'bg-red-500' : 'bg-gray-500'
                          }`}>
                            {index + 1}
                          </div>
                        </div>
                        <div className="flex-grow-1">
                          <h6 className="text-lg font-semibold text-gray-800 mb-4">{question.questionText}</h6>
                          
                          <div className="space-y-3">
                            {['A', 'B', 'C', 'D'].map((option) => {
                              const optionText = question[`option${option}`];
                              const isUserAnswer = userAnswer?.selectedOption === option;
                              const isCorrectAnswer = question.correctAnswer === option;
                              
                              let optionClass = 'p-3 rounded-lg border ';
                              if (isCorrectAnswer) {
                                optionClass += 'bg-green-100 border-green-300 text-green-800';
                              } else if (isUserAnswer && !isCorrectAnswer) {
                                optionClass += 'bg-red-100 border-red-300 text-red-800';
                              } else {
                                optionClass += 'bg-white border-gray-200 text-gray-700';
                              }
                              
                              return (
                                <div key={option} className={optionClass}>
                                  <div className="flex items-center justify-between">
                                    <div className="flex items-center">
                                      <span className="font-semibold mr-3">{option})</span>
                                      <span>{optionText}</span>
                                    </div>
                                    <div className="flex items-center space-x-2">
                                      {isCorrectAnswer && (
                                        <i className="fas fa-check text-green-600"></i>
                                      )}
                                      {isUserAnswer && !isCorrectAnswer && (
                                        <i className="fas fa-times text-red-600"></i>
                                      )}
                                      {isUserAnswer && isCorrectAnswer && (
                                        <i className="fas fa-check-double text-green-600"></i>
                                      )}
                                    </div>
                                  </div>
                                </div>
                              );
                            })}
                          </div>
                          
                          <div className="mt-4">
                            {!wasAnswered ? (
                              <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-yellow-100 text-yellow-800">
                                <i className="fas fa-minus mr-1"></i>
                                Not Answered
                              </span>
                            ) : isCorrect ? (
                              <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-green-100 text-green-800">
                                <i className="fas fa-check mr-1"></i>
                                Correct (+{question.marks || 1} mark{(question.marks || 1) > 1 ? 's' : ''})
                              </span>
                            ) : (
                              <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-red-100 text-red-800">
                                <i className="fas fa-times mr-1"></i>
                                Incorrect (0 marks)
                              </span>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ResultPage;
