import React, { useState, useEffect } from 'react';
import { quizAPI } from '../services/api';

const History = () => {
  const [loading, setLoading] = useState(true);
  const [historySummary, setHistorySummary] = useState([]);
  const [detailedHistory, setDetailedHistory] = useState([]);
  const [showDetailed, setShowDetailed] = useState(false);

  useEffect(() => {
    fetchHistoryData();
  }, []);

  const fetchHistoryData = async () => {
    try {
      const [summaryResponse, detailedResponse] = await Promise.all([
        quizAPI.getUserHistorySummary(),
        quizAPI.getUserHistory(),
      ]);
      setHistorySummary(summaryResponse.data);
      setDetailedHistory(detailedResponse.data);
    } catch (error) {
      console.error('Error fetching history:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getScoreColor = (score, total) => {
    const percentage = (score / total) * 100;
    if (percentage >= 80) return 'text-green-600';
    if (percentage >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getScoreColorBg = (score, total) => {
    const percentage = (score / total) * 100;
    if (percentage >= 80) return 'bg-green-100 text-green-800';
    if (percentage >= 60) return 'bg-yellow-100 text-yellow-800';
    return 'bg-red-100 text-red-800';
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-6xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Quiz History</h1>
          <p className="text-gray-600">Track your quiz performance and progress</p>
        </div>

        {/* Toggle between summary and detailed view */}
        <div className="mb-6">
          <div className="flex space-x-4">
            <button
              onClick={() => setShowDetailed(false)}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                !showDetailed
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              <i className="fas fa-chart-bar mr-2"></i>
              Summary View
            </button>
            <button
              onClick={() => setShowDetailed(true)}
              className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                showDetailed
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              <i className="fas fa-list mr-2"></i>
              Detailed View
            </button>
          </div>
        </div>

        {!showDetailed ? (
          /* Summary View */
          <div className="space-y-6">
            {historySummary.length === 0 ? (
              <div className="text-center py-12">
                <i className="fas fa-history text-gray-400 text-6xl mb-4"></i>
                <h3 className="text-xl font-semibold text-gray-700 mb-2">No Quiz History</h3>
                <p className="text-gray-500">You haven't taken any quizzes yet.</p>
              </div>
            ) : (
              historySummary.map((quiz) => (
                <div key={quiz.quizId} className="bg-white rounded-lg shadow-md p-6">
                  <div className="flex justify-between items-start mb-4">
                    <div className="flex-1">
                      <h3 className="text-xl font-semibold text-gray-900 mb-2">
                        {quiz.quizTitle}
                      </h3>
                      <p className="text-gray-600 mb-3">{quiz.quizDescription}</p>
                    </div>
                  </div>

                  <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
                    <div className="bg-blue-50 p-4 rounded-lg">
                      <div className="flex items-center">
                        <i className="fas fa-redo text-blue-600 text-lg mr-3"></i>
                        <div>
                          <p className="text-sm text-gray-600">Attempts</p>
                          <p className="text-2xl font-bold text-blue-600">{quiz.attemptCount}</p>
                        </div>
                      </div>
                    </div>

                    <div className="bg-green-50 p-4 rounded-lg">
                      <div className="flex items-center">
                        <i className="fas fa-trophy text-green-600 text-lg mr-3"></i>
                        <div>
                          <p className="text-sm text-gray-600">Best Score</p>
                          <p className={`text-2xl font-bold ${getScoreColor(quiz.bestScore, quiz.totalQuestions)}`}>
                            {quiz.bestScore}/{quiz.totalQuestions}
                          </p>
                        </div>
                      </div>
                    </div>

                    <div className="bg-yellow-50 p-4 rounded-lg">
                      <div className="flex items-center">
                        <i className="fas fa-chart-line text-yellow-600 text-lg mr-3"></i>
                        <div>
                          <p className="text-sm text-gray-600">Average Score</p>
                          <p className="text-2xl font-bold text-yellow-600">
                            {quiz.averageScore?.toFixed(1) || '0.0'}
                          </p>
                        </div>
                      </div>
                    </div>

                    <div className="bg-purple-50 p-4 rounded-lg">
                      <div className="flex items-center">
                        <i className="fas fa-clock text-purple-600 text-lg mr-3"></i>
                        <div>
                          <p className="text-sm text-gray-600">Latest Score</p>
                          <p className={`text-2xl font-bold ${getScoreColor(quiz.latestScore, quiz.totalQuestions)}`}>
                            {quiz.latestScore}/{quiz.totalQuestions}
                          </p>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="flex justify-between items-center text-sm text-gray-500">
                    <span>
                      <i className="fas fa-calendar-alt mr-1"></i>
                      First attempt: {formatDate(quiz.firstAttemptDate)}
                    </span>
                    <span>
                      <i className="fas fa-calendar-check mr-1"></i>
                      Last attempt: {formatDate(quiz.lastAttemptDate)}
                    </span>
                  </div>
                </div>
              ))
            )}
          </div>
        ) : (
          /* Detailed View */
          <div className="bg-white rounded-lg shadow-md">
            <div className="p-6 border-b border-gray-200">
              <h2 className="text-xl font-semibold text-gray-900">All Quiz Attempts</h2>
            </div>
            <div className="divide-y divide-gray-200">
              {detailedHistory.length === 0 ? (
                <div className="p-6 text-center">
                  <p className="text-gray-500">No quiz attempts found.</p>
                </div>
              ) : (
                detailedHistory.map((attempt) => (
                  <div key={attempt.id} className="p-6 hover:bg-gray-50 transition-colors">
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <h3 className="text-lg font-medium text-gray-900 mb-1">
                          {attempt.quizTitle}
                        </h3>
                        <div className="flex items-center space-x-4 text-sm text-gray-600 mb-2">
                          <span>
                            <i className="fas fa-calendar-alt mr-1"></i>
                            {formatDate(attempt.completedAt)}
                          </span>
                          <span>
                            <i className="fas fa-clock mr-1"></i>
                            {Math.floor(attempt.timeTakenSeconds / 60)}:{(attempt.timeTakenSeconds % 60).toString().padStart(2, '0')}
                          </span>
                        </div>
                        <div className="flex items-center space-x-4">
                          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getScoreColorBg(attempt.score, attempt.totalQuestions)}`}>
                            Score: {attempt.score}/{attempt.totalQuestions}
                          </span>
                          <span className="text-sm text-gray-600">
                            Correct: {attempt.correctAnswers} | Wrong: {attempt.wrongAnswers}
                          </span>
                        </div>
                      </div>
                      <div className="ml-4">
                        <a
                          href={`/result/${attempt.id}`}
                          className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                        >
                          <i className="fas fa-eye mr-2"></i>
                          View Details
                        </a>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default History;
