import React, { useState, useEffect } from 'react';
import { adminAPI } from '../services/api';
import QuestionForm from '../components/QuestionForm';
import QuizSummary from './QuizSummary';

const QuizManagement = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [showQuestionForm, setShowQuestionForm] = useState(false);
  const [editingQuestion, setEditingQuestion] = useState(null);
  const [showQuizForm, setShowQuizForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  const [quizFormData, setQuizFormData] = useState({
    title: '',
    description: '',
    durationMinutes: 30
  });

  useEffect(() => {
    fetchQuizzes();
  }, []);

  useEffect(() => {
    if (selectedQuiz) {
      fetchQuestions(selectedQuiz.id);
    }
  }, [selectedQuiz]);

  const fetchQuizzes = async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getAllQuizzes();
      setQuizzes(response.data);
    } catch (error) {
      setError('Error fetching quizzes');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchQuestions = async (quizId) => {
    try {
      const response = await adminAPI.getQuestionsByQuiz(quizId);
      setQuestions(response.data);
    } catch (error) {
      setError('Error fetching questions');
      console.error('Error:', error);
    }
  };

  const handleCreateQuiz = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const response = await adminAPI.createQuiz(quizFormData);
      setQuizzes([...quizzes, response.data]);
      setQuizFormData({ title: '', description: '', durationMinutes: 30 });
      setShowQuizForm(false);
    } catch (error) {
      setError('Error creating quiz');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteQuiz = async (quizId) => {
    if (window.confirm('Are you sure you want to delete this quiz? This will also delete all questions in it.')) {
      try {
        await adminAPI.deleteQuiz(quizId);
        setQuizzes(quizzes.filter(quiz => quiz.id !== quizId));
        if (selectedQuiz && selectedQuiz.id === quizId) {
          setSelectedQuiz(null);
          setQuestions([]);
        }
      } catch (error) {
        setError('Error deleting quiz');
        console.error('Error:', error);
      }
    }
  };

  const handleDeleteQuestion = async (questionId) => {
    if (window.confirm('Are you sure you want to delete this question?')) {
      try {
        await adminAPI.deleteQuestion(questionId);
        setQuestions(questions.filter(q => q.id !== questionId));
        // Update quiz total marks
        if (selectedQuiz) {
          fetchQuizzes();
        }
      } catch (error) {
        setError('Error deleting question');
        console.error('Error:', error);
      }
    }
  };

  const handleQuestionAdded = () => {
    if (selectedQuiz) {
      fetchQuestions(selectedQuiz.id);
      fetchQuizzes(); // Refresh to update total marks
    }
    setShowQuestionForm(false);
    setEditingQuestion(null);
  };

  return (
    <div className="container mx-auto mt-8 px-4">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">
          <i className="fas fa-cogs mr-3"></i>
          Quiz Management
        </h1>
        <button
          onClick={() => setShowQuizForm(true)}
          className="btn-primary"
        >
          <i className="fas fa-plus mr-2"></i>
          Create New Quiz
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-6">
          <i className="fas fa-exclamation-triangle mr-2"></i>
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Quiz List */}
        <div className="lg:col-span-1">
          <div className="quiz-card">
            <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white px-6 py-4 rounded-t-xl">
              <h3 className="text-lg font-semibold">
                <i className="fas fa-list mr-2"></i>
                Quizzes ({quizzes.length})
              </h3>
            </div>
            <div className="p-4 max-h-96 overflow-y-auto">
              {loading ? (
                <div className="text-center py-4">
                  <i className="fas fa-spinner fa-spin text-2xl text-gray-400"></i>
                </div>
              ) : quizzes.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <i className="fas fa-inbox text-3xl mb-3"></i>
                  <p>No quizzes created yet</p>
                </div>
              ) : (
                <div className="space-y-3">
                  {quizzes.map((quiz) => (
                    <div
                      key={quiz.id}
                      className={`p-4 border rounded-lg cursor-pointer transition-all hover:shadow-md ${
                        selectedQuiz?.id === quiz.id
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                      onClick={() => setSelectedQuiz(quiz)}
                    >
                      <div className="flex justify-between items-start">
                        <div className="flex-1">
                          <h4 className="font-semibold text-gray-800 mb-1">
                            {quiz.title}
                          </h4>
                          <p className="text-sm text-gray-600 mb-2">
                            {quiz.description}
                          </p>
                          <div className="flex items-center text-xs text-gray-500 space-x-4">
                            <span>
                              <i className="fas fa-clock mr-1"></i>
                              {quiz.durationMinutes}m
                            </span>
                            <span>
                              <i className="fas fa-star mr-1"></i>
                              {quiz.totalMarks} marks
                            </span>
                            <span className={`px-2 py-1 rounded ${
                              quiz.isActive ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-600'
                            }`}>
                              {quiz.isActive ? 'Active' : 'Inactive'}
                            </span>
                          </div>
                        </div>
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeleteQuiz(quiz.id);
                          }}
                          className="text-red-500 hover:text-red-700 ml-2"
                        >
                          <i className="fas fa-trash"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Questions Management */}
        <div className="lg:col-span-2">
          {selectedQuiz ? (
            <div className="space-y-6">
              {/* Quiz Header */}
              <div className="quiz-card">
                <div className="bg-gradient-to-r from-green-500 to-green-600 text-white px-6 py-4 rounded-t-xl">
                  <div className="flex justify-between items-center">
                    <div>
                      <h3 className="text-lg font-semibold">{selectedQuiz.title}</h3>
                      <p className="text-green-100">{selectedQuiz.description}</p>
                    </div>
                    <button
                      onClick={() => setShowQuestionForm(true)}
                      className="bg-white text-green-600 px-4 py-2 rounded-lg hover:bg-green-50 transition-colors"
                    >
                      <i className="fas fa-plus mr-2"></i>
                      Add Question
                    </button>
                  </div>
                </div>
                <div className="p-6">
                  <div className="grid grid-cols-3 gap-4 text-center">
                    <div>
                      <div className="text-2xl font-bold text-gray-800">{questions.length}</div>
                      <div className="text-sm text-gray-600">Questions</div>
                    </div>
                    <div>
                      <div className="text-2xl font-bold text-gray-800">{selectedQuiz.totalMarks}</div>
                      <div className="text-sm text-gray-600">Total Marks</div>
                    </div>
                    <div>
                      <div className="text-2xl font-bold text-gray-800">{selectedQuiz.durationMinutes}</div>
                      <div className="text-sm text-gray-600">Minutes</div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Questions List */}
              <div className="quiz-card">
                <div className="bg-gradient-to-r from-purple-500 to-purple-600 text-white px-6 py-4 rounded-t-xl">
                  <h3 className="text-lg font-semibold">
                    <i className="fas fa-question-circle mr-2"></i>
                    Questions
                  </h3>
                </div>
                <div className="p-6">
                  {questions.length === 0 ? (
                    <div className="text-center py-12 text-gray-500">
                      <i className="fas fa-question-circle text-4xl mb-4"></i>
                      <p className="text-lg mb-2">No questions added yet</p>
                      <p className="text-sm">Click "Add Question" to create your first question</p>
                    </div>
                  ) : (
                    <div className="space-y-4">
                      {questions.map((question, index) => (
                        <div key={question.id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                          <div className="flex justify-between items-start mb-3">
                            <div className="flex items-start space-x-3">
                              <div className="bg-blue-100 text-blue-800 rounded-full w-8 h-8 flex items-center justify-center font-semibold text-sm">
                                {index + 1}
                              </div>
                              <div className="flex-1">
                                <h4 className="font-semibold text-gray-800 mb-2">
                                  {question.questionText}
                                </h4>
                                <div className="grid grid-cols-2 gap-2 text-sm">
                                  {['A', 'B', 'C', 'D'].map((option) => (
                                    <div
                                      key={option}
                                      className={`p-2 rounded border ${
                                        question.correctAnswer === option
                                          ? 'bg-green-100 border-green-300 text-green-800'
                                          : 'bg-gray-50 border-gray-200'
                                      }`}
                                    >
                                      <strong>{option}:</strong> {question[`option${option}`]}
                                      {question.correctAnswer === option && (
                                        <i className="fas fa-check ml-2 text-green-600"></i>
                                      )}
                                    </div>
                                  ))}
                                </div>
                              </div>
                            </div>
                            <div className="flex items-center space-x-2">
                              <span className="bg-yellow-100 text-yellow-800 px-2 py-1 rounded text-xs">
                                {question.marks} mark{question.marks !== 1 ? 's' : ''}
                              </span>
                              <button
                                onClick={() => {
                                  setEditingQuestion(question);
                                  setShowQuestionForm(true);
                                }}
                                className="text-blue-500 hover:text-blue-700"
                              >
                                <i className="fas fa-edit"></i>
                              </button>
                              <button
                                onClick={() => handleDeleteQuestion(question.id)}
                                className="text-red-500 hover:text-red-700"
                              >
                                <i className="fas fa-trash"></i>
                              </button>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
          ) : (
            <div className="quiz-card">
              <div className="p-12 text-center text-gray-500">
                <i className="fas fa-arrow-left text-4xl mb-4"></i>
                <p className="text-lg">Select a quiz to manage questions</p>
                <p className="text-sm">Choose a quiz from the left panel to view and edit its questions</p>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Quiz Creation Modal */}
      {showQuizForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl max-w-md w-full">
            <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white px-6 py-4 rounded-t-xl">
              <h3 className="text-lg font-semibold">Create New Quiz</h3>
            </div>
            <form onSubmit={handleCreateQuiz} className="p-6">
              <div className="mb-4">
                <label className="form-label">Quiz Title</label>
                <input
                  type="text"
                  className="form-control"
                  value={quizFormData.title}
                  onChange={(e) => setQuizFormData({...quizFormData, title: e.target.value})}
                  required
                />
              </div>
              <div className="mb-4">
                <label className="form-label">Description</label>
                <textarea
                  className="form-control"
                  rows="3"
                  value={quizFormData.description}
                  onChange={(e) => setQuizFormData({...quizFormData, description: e.target.value})}
                  required
                />
              </div>
              <div className="mb-6">
                <label className="form-label">Duration (minutes)</label>
                <input
                  type="number"
                  className="form-control"
                  value={quizFormData.durationMinutes}
                  onChange={(e) => setQuizFormData({...quizFormData, durationMinutes: parseInt(e.target.value)})}
                  min="1"
                  required
                />
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  className="btn-outline"
                  onClick={() => setShowQuizForm(false)}
                >
                  Cancel
                </button>
                <button type="submit" className="btn-primary" disabled={loading}>
                  {loading ? 'Creating...' : 'Create Quiz'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Question Form Modal */}
      {showQuestionForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl max-w-4xl w-full max-h-screen overflow-y-auto">
            <QuestionForm
              quizId={selectedQuiz.id}
              existingQuestion={editingQuestion}
              onQuestionAdded={handleQuestionAdded}
              onClose={() => {
                setShowQuestionForm(false);
                setEditingQuestion(null);
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default QuizManagement;
