import React, { useState, useEffect } from 'react';
import { adminAPI } from '../services/api';

const QuestionForm = ({ quizId, onQuestionAdded, onClose, existingQuestion = null }) => {
  const [formData, setFormData] = useState({
    questionText: '',
    optionA: '',
    optionB: '',
    optionC: '',
    optionD: '',
    correctAnswer: '',
    marks: 1,
    quizId: quizId
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (existingQuestion) {
      setFormData({
        questionText: existingQuestion.questionText || '',
        optionA: existingQuestion.optionA || '',
        optionB: existingQuestion.optionB || '',
        optionC: existingQuestion.optionC || '',
        optionD: existingQuestion.optionD || '',
        correctAnswer: existingQuestion.correctAnswer || '',
        marks: existingQuestion.marks || 1,
        quizId: quizId
      });
    }
  }, [existingQuestion, quizId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.questionText.trim()) {
      newErrors.questionText = 'Question text is required';
    }
    
    if (!formData.optionA.trim()) {
      newErrors.optionA = 'Option A is required';
    }
    
    if (!formData.optionB.trim()) {
      newErrors.optionB = 'Option B is required';
    }
    
    if (!formData.optionC.trim()) {
      newErrors.optionC = 'Option C is required';
    }
    
    if (!formData.optionD.trim()) {
      newErrors.optionD = 'Option D is required';
    }
    
    if (!formData.correctAnswer) {
      newErrors.correctAnswer = 'Please select the correct answer';
    }
    
    if (!formData.marks || formData.marks < 1) {
      newErrors.marks = 'Marks must be at least 1';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    try {
      setLoading(true);
      
      if (existingQuestion) {
        await adminAPI.updateQuestion(existingQuestion.id, formData);
      } else {
        await adminAPI.createQuestion(formData);
      }
      
      // Reset form
      setFormData({
        questionText: '',
        optionA: '',
        optionB: '',
        optionC: '',
        optionD: '',
        correctAnswer: '',
        marks: 1,
        quizId: quizId
      });
      
      if (onQuestionAdded) {
        onQuestionAdded();
      }
      
      if (onClose) {
        onClose();
      }
      
    } catch (error) {
      console.error('Error saving question:', error);
      setErrors({ submit: 'Error saving question. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  const getOptionStyle = (option) => {
    const baseStyle = "form-control";
    if (formData.correctAnswer === option) {
      return `${baseStyle} border-success bg-success bg-opacity-10`;
    }
    return baseStyle;
  };

  const getOptionLabelStyle = (option) => {
    if (formData.correctAnswer === option) {
      return "form-label text-success fw-bold";
    }
    return "form-label";
  };

  return (
    <div className="quiz-card">
      <div className="bg-gradient-to-r from-primary-500 to-primary-600 text-white px-6 py-4 rounded-t-xl">
        <h5 className="text-xl font-semibold flex items-center">
          <i className="fas fa-question-circle mr-2"></i>
          {existingQuestion ? 'Edit Question' : 'Add New Question'}
        </h5>
      </div>
      
      <div className="p-6">
        <form onSubmit={handleSubmit}>
          {/* Question Text */}
          <div className="mb-6">
            <label className="form-label text-lg font-semibold text-gray-700">
              <i className="fas fa-question mr-2"></i>
              Question Text
            </label>
            <textarea
              name="questionText"
              value={formData.questionText}
              onChange={handleChange}
              className={`form-control ${errors.questionText ? 'border-red-500' : ''}`}
              rows="3"
              placeholder="Enter your question here..."
            />
            {errors.questionText && (
              <div className="text-red-500 text-sm mt-1">
                <i className="fas fa-exclamation-circle mr-1"></i>
                {errors.questionText}
              </div>
            )}
          </div>

          {/* Options */}
          <div className="mb-6">
            <label className="form-label text-lg font-semibold text-gray-700 mb-4 block">
              <i className="fas fa-list mr-2"></i>
              Answer Options
              <small className="text-gray-500 font-normal ml-2">
                (Click on the correct option to select it)
              </small>
            </label>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Option A */}
              <div className="space-y-2">
                <label className={getOptionLabelStyle('A')}>
                  <input
                    type="radio"
                    name="correctAnswer"
                    value="A"
                    checked={formData.correctAnswer === 'A'}
                    onChange={handleChange}
                    className="form-check-input me-2"
                  />
                  Option A {formData.correctAnswer === 'A' && 'âœ“'}
                </label>
                <input
                  type="text"
                  name="optionA"
                  value={formData.optionA}
                  onChange={handleChange}
                  className={getOptionStyle('A')}
                  placeholder="Enter option A"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'A' }))}
                />
                {errors.optionA && (
                  <div className="text-red-500 text-sm">
                    <i className="fas fa-exclamation-circle mr-1"></i>
                    {errors.optionA}
                  </div>
                )}
              </div>

              {/* Option B */}
              <div className="space-y-2">
                <label className={getOptionLabelStyle('B')}>
                  <input
                    type="radio"
                    name="correctAnswer"
                    value="B"
                    checked={formData.correctAnswer === 'B'}
                    onChange={handleChange}
                    className="form-check-input me-2"
                  />
                  Option B {formData.correctAnswer === 'B' && 'âœ“'}
                </label>
                <input
                  type="text"
                  name="optionB"
                  value={formData.optionB}
                  onChange={handleChange}
                  className={getOptionStyle('B')}
                  placeholder="Enter option B"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'B' }))}
                />
                {errors.optionB && (
                  <div className="text-red-500 text-sm">
                    <i className="fas fa-exclamation-circle mr-1"></i>
                    {errors.optionB}
                  </div>
                )}
              </div>

              {/* Option C */}
              <div className="space-y-2">
                <label className={getOptionLabelStyle('C')}>
                  <input
                    type="radio"
                    name="correctAnswer"
                    value="C"
                    checked={formData.correctAnswer === 'C'}
                    onChange={handleChange}
                    className="form-check-input me-2"
                  />
                  Option C {formData.correctAnswer === 'C' && 'âœ“'}
                </label>
                <input
                  type="text"
                  name="optionC"
                  value={formData.optionC}
                  onChange={handleChange}
                  className={getOptionStyle('C')}
                  placeholder="Enter option C"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'C' }))}
                />
                {errors.optionC && (
                  <div className="text-red-500 text-sm">
                    <i className="fas fa-exclamation-circle mr-1"></i>
                    {errors.optionC}
                  </div>
                )}
              </div>

              {/* Option D */}
              <div className="space-y-2">
                <label className={getOptionLabelStyle('D')}>
                  <input
                    type="radio"
                    name="correctAnswer"
                    value="D"
                    checked={formData.correctAnswer === 'D'}
                    onChange={handleChange}
                    className="form-check-input me-2"
                  />
                  Option D {formData.correctAnswer === 'D' && 'âœ“'}
                </label>
                <input
                  type="text"
                  name="optionD"
                  value={formData.optionD}
                  onChange={handleChange}
                  className={getOptionStyle('D')}
                  placeholder="Enter option D"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'D' }))}
                />
                {errors.optionD && (
                  <div className="text-red-500 text-sm">
                    <i className="fas fa-exclamation-circle mr-1"></i>
                    {errors.optionD}
                  </div>
                )}
              </div>
            </div>
            
            {errors.correctAnswer && (
              <div className="text-red-500 text-sm mt-2">
                <i className="fas fa-exclamation-circle mr-1"></i>
                {errors.correctAnswer}
              </div>
            )}
          </div>

          {/* Marks */}
          <div className="mb-6">
            <label className="form-label text-lg font-semibold text-gray-700">
              <i className="fas fa-star mr-2"></i>
              Marks
            </label>
            <input
              type="number"
              name="marks"
              value={formData.marks}
              onChange={handleChange}
              className={`form-control w-32 ${errors.marks ? 'border-red-500' : ''}`}
              min="1"
              max="10"
            />
            {errors.marks && (
              <div className="text-red-500 text-sm mt-1">
                <i className="fas fa-exclamation-circle mr-1"></i>
                {errors.marks}
              </div>
            )}
          </div>

          {/* Correct Answer Summary */}
          {formData.correctAnswer && (
            <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg">
              <div className="flex items-center text-green-800">
                <i className="fas fa-check-circle mr-2"></i>
                <strong>Correct Answer: Option {formData.correctAnswer}</strong>
              </div>
              <div className="text-green-700 mt-1">
                "{formData[`option${formData.correctAnswer}`]}"
              </div>
            </div>
          )}

          {/* Error Message */}
          {errors.submit && (
            <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
              <i className="fas fa-exclamation-triangle mr-2"></i>
              {errors.submit}
            </div>
          )}

          {/* Buttons */}
          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="btn-outline"
              disabled={loading}
            >
              <i className="fas fa-times mr-2"></i>
              Cancel
            </button>
            <button
              type="submit"
              className="btn-primary"
              disabled={loading}
            >
              {loading ? (
                <>
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Saving...
                </>
              ) : (
                <>
                  <i className="fas fa-save mr-2"></i>
                  {existingQuestion ? 'Update Question' : 'Add Question'}
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default QuestionForm;
