import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { quizAPI } from '../services/api';

const QuizPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  
  const [quiz, setQuiz] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [quizStarted, setQuizStarted] = useState(false);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [selectedAnswer, setSelectedAnswer] = useState('');

  const fetchQuizData = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      
      const [quizResponse, questionsResponse] = await Promise.all([
        quizAPI.getQuiz(id),
        quizAPI.getQuizQuestions(id)
      ]);
      
      setQuiz(quizResponse.data);
      setQuestions(questionsResponse.data);
      setTimeLeft(quizResponse.data.durationMinutes * 60);
    } catch (error) {
      console.error('Error loading quiz:', error);
      setError(`Error loading quiz: ${error.message || 'Unknown error'}`);
    } finally {
      setLoading(false);
    }
  }, [id]);

  const handleSubmitQuiz = useCallback(async () => {
    if (submitting) return;
    
    setSubmitting(true);
    const startTime = quiz.durationMinutes * 60;
    const timeTaken = startTime - timeLeft;

    try {
      const response = await quizAPI.submitQuiz({
        quizId: parseInt(id),
        answers,
        timeTakenSeconds: timeTaken
      });
      
      navigate(`/result/${response.data.id}`);
    } catch (error) {
      console.error('Quiz submission error:', error);
      if (error.message) {
        setError(error.message);
      } else if (error.response?.data?.message) {
        setError(error.response.data.message);
      } else {
        setError('Error submitting quiz. Please try again.');
      }
      setSubmitting(false);
    }
  }, [id, quiz, answers, timeLeft, navigate, submitting]);

  useEffect(() => {
    fetchQuizData();
  }, [fetchQuizData]);

  useEffect(() => {
    if (quizStarted && timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            handleSubmitQuiz();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [quizStarted, timeLeft, handleSubmitQuiz]);

  const startQuiz = () => {
    setQuizStarted(true);
  };

  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
    setAnswers(prev => ({
      ...prev,
      [questions[currentQuestionIndex].id]: answer
    }));
  };

  const navigateToQuestion = (index) => {
    if (submitting) return;
    
    if (index >= 0 && index < questions.length) {
      setCurrentQuestionIndex(index);
      setSelectedAnswer(answers[questions[index]?.id] || '');
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-900 to-indigo-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-white text-lg">Loading quiz...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-red-900 to-red-800 flex items-center justify-center">
        <div className="text-center bg-white/10 backdrop-blur-md rounded-xl p-8 border border-white/20">
          <h2 className="text-2xl font-bold text-white mb-4">Error</h2>
          <p className="text-red-200 mb-6">{error}</p>
          <div className="space-y-3">
            <button
              onClick={() => fetchQuizData()}
              className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-lg transition-colors duration-300 mr-3"
            >
              Try Again
            </button>
            <button
              onClick={() => navigate('/quizzes')}
              className="bg-gray-500 hover:bg-gray-600 text-white px-6 py-3 rounded-lg transition-colors duration-300"
            >
              Back to Quizzes
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (!quiz || questions.length === 0) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 flex items-center justify-center">
        <div className="text-center bg-white/10 backdrop-blur-md rounded-xl p-8 border border-white/20">
          <h2 className="text-2xl font-bold text-white mb-4">Quiz Not Found</h2>
          <p className="text-gray-200 mb-6">The quiz you're looking for doesn't exist or has no questions.</p>
          <button
            onClick={() => navigate('/quizzes')}
            className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-lg transition-colors duration-300"
          >
            Back to Quizzes
          </button>
        </div>
      </div>
    );
  }

  if (!quizStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-900 to-indigo-900 flex items-center justify-center px-4">
        <div className="max-w-2xl w-full">
          <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 border border-white/20 text-center">
            <h1 className="text-3xl font-bold text-white mb-4">{quiz?.title}</h1>
            <p className="text-blue-200 mb-8">{quiz?.description}</p>

            <div className="grid md:grid-cols-3 gap-6 mb-8">
              <div className="bg-white/10 rounded-lg p-4">
                <p className="text-white text-sm">Duration</p>
                <p className="text-blue-200 font-bold">{quiz?.durationMinutes} minutes</p>
              </div>
              <div className="bg-white/10 rounded-lg p-4">
                <p className="text-white text-sm">Questions</p>
                <p className="text-green-200 font-bold">{questions.length}</p>
              </div>
              <div className="bg-white/10 rounded-lg p-4">
                <p className="text-white text-sm">Total Points</p>
                <p className="text-yellow-200 font-bold">{quiz?.totalMarks}</p>
              </div>
            </div>

            <button
              onClick={startQuiz}
              className="bg-gradient-to-r from-green-500 to-blue-600 hover:from-green-600 hover:to-blue-700 text-white font-bold py-4 px-8 rounded-lg transition-all duration-300"
            >
              Start Quiz
            </button>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestion = questions[currentQuestionIndex];
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900">
      {/* Header */}
      <div className="bg-black/20 backdrop-blur-md border-b border-white/10 sticky top-0 z-10">
        <div className="max-w-6xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate('/quizzes')}
                className="flex items-center px-3 py-2 rounded-lg bg-white/10 hover:bg-white/20 text-white transition-all duration-300"
              >
                ← Back
              </button>
              <h1 className="text-xl font-bold text-white">{quiz?.title}</h1>
            </div>
            
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2 px-3 py-2 rounded-lg bg-black/20 text-white">
                <span>⏱️</span>
                <span className="font-mono text-lg font-bold">
                  {formatTime(timeLeft)}
                </span>
              </div>
              <span className="text-white/80 text-sm">
                {currentQuestionIndex + 1} / {questions.length}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 border border-white/20">
          
          {/* Question */}
          <div className="mb-8">
            <h2 className="text-2xl font-semibold text-white mb-6">
              {currentQuestion?.questionText}
            </h2>

            {/* Answer Options */}
            <div className="grid gap-4">
              {['A', 'B', 'C', 'D'].map((option) => {
                const optionText = currentQuestion?.[`option${option}`];
                const isSelected = selectedAnswer === option;
                
                return optionText ? (
                  <button
                    key={option}
                    onClick={() => handleAnswerSelect(option)}
                    disabled={submitting}
                    className={`p-4 rounded-xl border-2 text-left transition-all duration-300 ${
                      isSelected 
                        ? 'border-blue-400 bg-blue-500/20 text-blue-300'
                        : 'border-white/30 bg-white/10 text-white hover:border-white/50 hover:bg-white/20'
                    } ${submitting ? 'cursor-not-allowed' : 'cursor-pointer'}`}
                  >
                    <div className="flex items-center">
                      <span className={`w-8 h-8 rounded-full border-2 flex items-center justify-center mr-4 font-bold text-sm ${
                        isSelected 
                          ? 'border-blue-400 bg-blue-500 text-white' 
                          : 'border-white/50'
                      }`}>
                        {option}
                      </span>
                      <span className="flex-1">{optionText}</span>
                    </div>
                  </button>
                ) : null;
              })}
            </div>
          </div>

          {/* Navigation */}
          <div className="flex items-center justify-between">
            <button
              onClick={() => navigateToQuestion(currentQuestionIndex - 1)}
              disabled={currentQuestionIndex === 0 || submitting}
              className="flex items-center px-6 py-3 bg-white/10 hover:bg-white/20 disabled:bg-white/5 text-white disabled:text-white/40 rounded-lg transition-all duration-300 disabled:cursor-not-allowed"
            >
              ← Previous
            </button>

            {currentQuestionIndex === questions.length - 1 ? (
              <button
                onClick={handleSubmitQuiz}
                disabled={submitting || Object.keys(answers).length === 0}
                className="flex items-center px-6 py-3 bg-gradient-to-r from-green-500 to-blue-600 hover:from-green-600 hover:to-blue-700 disabled:from-gray-500 disabled:to-gray-600 text-white font-bold rounded-lg transition-all duration-300 disabled:cursor-not-allowed"
              >
                {submitting ? 'Submitting...' : 'Submit Quiz'}
              </button>
            ) : (
              <button
                onClick={() => navigateToQuestion(currentQuestionIndex + 1)}
                disabled={currentQuestionIndex === questions.length - 1 || submitting}
                className="flex items-center px-6 py-3 bg-white/10 hover:bg-white/20 disabled:bg-white/5 text-white disabled:text-white/40 rounded-lg transition-all duration-300 disabled:cursor-not-allowed"
              >
                Next →
              </button>
            )}
          </div>

          {/* Question Navigator */}
          <div className="mt-8 pt-6 border-t border-white/20">
            <h3 className="text-white font-semibold mb-3">Questions</h3>
            <div className="grid grid-cols-10 gap-2">
              {questions.map((_, index) => {
                const isAnswered = answers[questions[index]?.id];
                const isCurrent = index === currentQuestionIndex;
                
                return (
                  <button
                    key={index}
                    onClick={() => navigateToQuestion(index)}
                    disabled={submitting}
                    className={`w-10 h-10 rounded-lg font-bold text-sm transition-all duration-300 ${
                      isCurrent 
                        ? 'bg-blue-500 text-white border-2 border-blue-300' 
                        : isAnswered 
                        ? 'bg-green-500/50 text-white border border-green-400' 
                        : 'bg-white/20 text-white/80 border border-white/30 hover:bg-white/30'
                    } ${submitting ? 'cursor-not-allowed opacity-50' : 'cursor-pointer'}`}
                  >
                    {index + 1}
                  </button>
                );
              })}
            </div>
          </div>
        </div>
      </div>

      {/* Submit Modal */}
      {submitting && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50">
          <div className="bg-white/10 backdrop-blur-md rounded-xl p-8 border border-white/20 text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
            <p className="text-white text-lg">Submitting your quiz...</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default QuizPage;
