import React, { useState, useEffect, useCallback } from 'react';
import { adminAPI } from '../services/api';
import QuizManagement from '../components/QuizManagement';

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [quizzes, setQuizzes] = useState([]);
  const [users, setUsers] = useState([]);
  const [reports, setReports] = useState({});
  const [dashboardStats, setDashboardStats] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (activeTab === 'quizzes' || activeTab === 'dashboard') {
      fetchQuizzes();
    }
    if (activeTab === 'dashboard') {
      fetchDashboardStats();
    }
    if (activeTab === 'users') {
      fetchUsers();
    }
    if (activeTab === 'reports') {
      fetchReports();
    }
  }, [activeTab]);

  const fetchDashboardStats = async () => {
    try {
      const response = await adminAPI.getDashboardStats();
      setDashboardStats(response.data);
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
    }
  };

  const fetchQuizzes = async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getAllQuizzes();
      setQuizzes(response.data);
    } catch (error) {
      setError('Error fetching quizzes');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getAllUsersWithStats();
      setUsers(response.data);
    } catch (error) {
      setError('Error fetching users');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchReports = async () => {
    try {
      setLoading(true);
      const [userActivity, quizPerformance, recentActivity] = await Promise.all([
        adminAPI.getUserActivityReport(),
        adminAPI.getQuizPerformanceReport(),
        adminAPI.getRecentActivityReport(7)
      ]);
      
      setReports({
        userActivity: userActivity.data,
        quizPerformance: quizPerformance.data,
        recentActivity: recentActivity.data
      });
    } catch (error) {
      setError('Error fetching reports');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteQuiz = async (quizId) => {
    if (window.confirm('Are you sure you want to delete this quiz?')) {
      try {
        await adminAPI.deleteQuiz(quizId);
        setQuizzes(quizzes.filter(quiz => quiz.id !== quizId));
      } catch (error) {
        setError('Error deleting quiz');
        console.error('Error:', error);
      }
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user? This will also delete all their quiz results.')) {
      try {
        await adminAPI.deleteUser(userId);
        setUsers(users.filter(user => user.id !== userId));
      } catch (error) {
        setError('Error deleting user');
        console.error('Error:', error);
      }
    }
  };

  const handleUpdateUserRole = async (userId, newRole) => {
    try {
      await adminAPI.updateUserRole(userId, newRole);
      setUsers(users.map(user => 
        user.id === userId ? { ...user, role: newRole } : user
      ));
    } catch (error) {
      setError('Error updating user role');
      console.error('Error:', error);
    }
  };

  const renderDashboard = () => (
    <div className="row">
      <div className="col-md-12">
        <h2>Admin Dashboard</h2>
        <div className="row mt-4">
          <div className="col-md-3">
            <div className="card bg-primary text-white">
              <div className="card-body text-center">
                <i className="fas fa-list-alt fa-3x mb-3"></i>
                <h3>{quizzes.length}</h3>
                <p>Total Quizzes</p>
              </div>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card bg-success text-white">
              <div className="card-body text-center">
                <i className="fas fa-question fa-3x mb-3"></i>
                <h3>{quizzes.reduce((sum, quiz) => sum + (quiz.questions?.length || 0), 0)}</h3>
                <p>Total Questions</p>
              </div>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card bg-warning text-white">
              <div className="card-body text-center">
                <i className="fas fa-users fa-3x mb-3"></i>
                <h3>0</h3>
                <p>Active Users</p>
              </div>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card bg-info text-white">
              <div className="card-body text-center">
                <i className="fas fa-chart-bar fa-3x mb-3"></i>
                <h3>0</h3>
                <p>Quiz Attempts</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderQuizManagement = () => (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Quiz Management</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setActiveTab('create-quiz')}
        >
          <i className="fas fa-plus me-2"></i>Create New Quiz
        </button>
      </div>

      {loading ? (
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Description</th>
                <th>Duration (min)</th>
                <th>Questions</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {quizzes.map(quiz => (
                <tr key={quiz.id}>
                  <td>{quiz.id}</td>
                  <td>{quiz.title}</td>
                  <td>{quiz.description || 'No description'}</td>
                  <td>{quiz.durationMinutes}</td>
                  <td>{quiz.questions?.length || 0}</td>
                  <td>
                    <span className={`badge ${quiz.isActive ? 'bg-success' : 'bg-secondary'}`}>
                      {quiz.isActive ? 'Active' : 'Inactive'}
                    </span>
                  </td>
                  <td>
                    <div className="btn-group" role="group">
                      <button 
                        className="btn btn-sm btn-outline-primary"
                        onClick={() => setActiveTab(`questions-${quiz.id}`)}
                      >
                        <i className="fas fa-question-circle"></i>
                      </button>
                      <button 
                        className="btn btn-sm btn-outline-warning"
                        onClick={() => setActiveTab(`edit-quiz-${quiz.id}`)}
                      >
                        <i className="fas fa-edit"></i>
                      </button>
                      <button 
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => handleDeleteQuiz(quiz.id)}
                      >
                        <i className="fas fa-trash"></i>
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );

  const renderCreateQuiz = () => (
    <CreateQuizForm 
      onSuccess={() => {
        setActiveTab('quizzes');
        fetchQuizzes();
      }}
      onCancel={() => setActiveTab('quizzes')}
    />
  );

  const renderQuestionManagement = (quizId) => (
    <QuestionManagement 
      quizId={quizId}
      onBack={() => setActiveTab('quizzes')}
    />
  );

  return (
    <div className="container-fluid mt-4">
      <div className="row">
        {/* Sidebar */}
        <div className="col-md-3">
          <div className="card">
            <div className="card-header">
              <h5><i className="fas fa-cog me-2"></i>Admin Panel</h5>
            </div>
            <div className="list-group list-group-flush">
              <button
                className={`list-group-item list-group-item-action ${activeTab === 'dashboard' ? 'active' : ''}`}
                onClick={() => setActiveTab('dashboard')}
              >
                <i className="fas fa-tachometer-alt me-2"></i>Dashboard
              </button>
              <button
                className={`list-group-item list-group-item-action ${activeTab === 'quiz-management' ? 'active' : ''}`}
                onClick={() => setActiveTab('quiz-management')}
              >
                <i className="fas fa-cogs me-2"></i>Quiz Builder
              </button>
              <button
                className={`list-group-item list-group-item-action ${activeTab === 'quizzes' ? 'active' : ''}`}
                onClick={() => setActiveTab('quizzes')}
              >
                <i className="fas fa-list-alt me-2"></i>Quiz Management (Old)
              </button>
              <button
                className={`list-group-item list-group-item-action ${activeTab === 'users' ? 'active' : ''}`}
                onClick={() => setActiveTab('users')}
              >
                <i className="fas fa-users me-2"></i>User Management
              </button>
              <button
                className={`list-group-item list-group-item-action ${activeTab === 'reports' ? 'active' : ''}`}
                onClick={() => setActiveTab('reports')}
              >
                <i className="fas fa-chart-bar me-2"></i>Reports
              </button>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="col-md-9">
          <div className="card">
            <div className="card-body">
              {error && (
                <div className="alert alert-danger" role="alert">
                  {error}
                </div>
              )}

              {activeTab === 'dashboard' && renderDashboard()}
              {activeTab === 'quiz-management' && <QuizManagement />}
              {activeTab === 'quizzes' && renderQuizManagement()}
              {activeTab === 'create-quiz' && renderCreateQuiz()}
              {activeTab.startsWith('questions-') && renderQuestionManagement(activeTab.split('-')[1])}
              
              {activeTab === 'users' && (
                <div>
                  <div className="d-flex justify-content-between align-items-center mb-4">
                    <h2>User Management</h2>
                    <div className="d-flex gap-2">
                      <button className="btn btn-outline-primary">
                        <i className="fas fa-download me-2"></i>Export Users
                      </button>
                    </div>
                  </div>

                  {loading ? (
                    <div className="text-center">
                      <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading...</span>
                      </div>
                    </div>
                  ) : (
                    <div className="table-responsive">
                      <table className="table table-striped">
                        <thead>
                          <tr>
                            <th>ID</th>
                            <th>Username</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Total Attempts</th>
                            <th>Avg Score</th>
                            <th>Best Score</th>
                            <th>Created</th>
                            <th>Actions</th>
                          </tr>
                        </thead>
                        <tbody>
                          {users.map((user) => (
                            <tr key={user.id}>
                              <td>{user.id}</td>
                              <td>
                                <div className="d-flex align-items-center">
                                  <div className="me-2">
                                    <i className="fas fa-user-circle text-muted fa-lg"></i>
                                  </div>
                                  <strong>{user.username}</strong>
                                </div>
                              </td>
                              <td>{user.email}</td>
                              <td>
                                <select 
                                  className={`form-select form-select-sm ${user.role === 'ADMIN' ? 'bg-warning' : 'bg-light'}`}
                                  value={user.role}
                                  onChange={(e) => handleUpdateUserRole(user.id, e.target.value)}
                                >
                                  <option value="USER">User</option>
                                  <option value="ADMIN">Admin</option>
                                </select>
                              </td>
                              <td>
                                <span className="badge bg-info">{user.totalAttempts}</span>
                              </td>
                              <td>
                                <span className={`badge ${user.averageScore >= 8 ? 'bg-success' : user.averageScore >= 5 ? 'bg-warning' : 'bg-danger'}`}>
                                  {user.averageScore ? user.averageScore.toFixed(1) : '0.0'}
                                </span>
                              </td>
                              <td>
                                <span className="badge bg-primary">{user.bestScore}</span>
                              </td>
                              <td>
                                {user.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'N/A'}
                              </td>
                              <td>
                                <div className="btn-group btn-group-sm">
                                  <button 
                                    className="btn btn-outline-info"
                                    title="View Details"
                                  >
                                    <i className="fas fa-eye"></i>
                                  </button>
                                  <button 
                                    className="btn btn-outline-danger"
                                    onClick={() => handleDeleteUser(user.id)}
                                    title="Delete User"
                                  >
                                    <i className="fas fa-trash"></i>
                                  </button>
                                </div>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                      
                      {users.length === 0 && (
                        <div className="text-center text-muted py-4">
                          <i className="fas fa-users fa-3x mb-3"></i>
                          <p>No users found</p>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              )}
              
              {activeTab === 'reports' && (
                <div>
                  <h2 className="mb-4">Reports & Analytics</h2>
                  
                  {loading ? (
                    <div className="text-center">
                      <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading reports...</span>
                      </div>
                    </div>
                  ) : (
                    <div>
                      {/* User Activity Report */}
                      <div className="card mb-4">
                        <div className="card-header">
                          <h5 className="mb-0">
                            <i className="fas fa-chart-line me-2"></i>
                            User Activity Report
                          </h5>
                        </div>
                        <div className="card-body">
                          <div className="table-responsive">
                            <table className="table table-sm">
                              <thead>
                                <tr>
                                  <th>Username</th>
                                  <th>Total Attempts</th>
                                  <th>Total Score</th>
                                  <th>Average Score</th>
                                  <th>Last Activity</th>
                                </tr>
                              </thead>
                              <tbody>
                                {reports.userActivity?.slice(0, 10).map((activity, index) => (
                                  <tr key={index}>
                                    <td>
                                      <strong>{activity.username}</strong>
                                    </td>
                                    <td>
                                      <span className="badge bg-info">{activity.totalAttempts}</span>
                                    </td>
                                    <td>
                                      <span className="badge bg-primary">{activity.totalScore}</span>
                                    </td>
                                    <td>
                                      <span className={`badge ${activity.averageScore >= 8 ? 'bg-success' : activity.averageScore >= 5 ? 'bg-warning' : 'bg-danger'}`}>
                                        {activity.averageScore ? Number(activity.averageScore).toFixed(1) : '0.0'}
                                      </span>
                                    </td>
                                    <td>
                                      {activity.lastAttempt ? new Date(activity.lastAttempt).toLocaleDateString() : 'N/A'}
                                    </td>
                                  </tr>
                                ))}
                              </tbody>
                            </table>
                          </div>
                        </div>
                      </div>

                      {/* Quiz Performance Report */}
                      <div className="card mb-4">
                        <div className="card-header">
                          <h5 className="mb-0">
                            <i className="fas fa-chart-bar me-2"></i>
                            Quiz Performance Report
                          </h5>
                        </div>
                        <div className="card-body">
                          <div className="table-responsive">
                            <table className="table table-sm">
                              <thead>
                                <tr>
                                  <th>Quiz Title</th>
                                  <th>Total Attempts</th>
                                  <th>Average Score</th>
                                  <th>Highest Score</th>
                                  <th>Lowest Score</th>
                                  <th>Pass Rate</th>
                                </tr>
                              </thead>
                              <tbody>
                                {reports.quizPerformance?.map((performance, index) => (
                                  <tr key={index}>
                                    <td>
                                      <strong>{performance.quizTitle}</strong>
                                    </td>
                                    <td>
                                      <span className="badge bg-info">{performance.totalAttempts}</span>
                                    </td>
                                    <td>
                                      <span className="badge bg-primary">
                                        {performance.averageScore ? Number(performance.averageScore).toFixed(1) : '0.0'}
                                      </span>
                                    </td>
                                    <td>
                                      <span className="badge bg-success">{performance.highestScore}</span>
                                    </td>
                                    <td>
                                      <span className="badge bg-danger">{performance.lowestScore}</span>
                                    </td>
                                    <td>
                                      <span className={`badge ${performance.passRate >= 70 ? 'bg-success' : performance.passRate >= 50 ? 'bg-warning' : 'bg-danger'}`}>
                                        {performance.passRate ? Number(performance.passRate).toFixed(1) : '0.0'}%
                                      </span>
                                    </td>
                                  </tr>
                                ))}
                              </tbody>
                            </table>
                          </div>
                        </div>
                      </div>

                      {/* Recent Activity */}
                      <div className="card">
                        <div className="card-header">
                          <h5 className="mb-0">
                            <i className="fas fa-clock me-2"></i>
                            Recent Activity (Last 7 Days)
                          </h5>
                        </div>
                        <div className="card-body">
                          <div className="table-responsive">
                            <table className="table table-sm">
                              <thead>
                                <tr>
                                  <th>User</th>
                                  <th>Quiz</th>
                                  <th>Score</th>
                                  <th>Percentage</th>
                                  <th>Time Taken</th>
                                  <th>Completed At</th>
                                </tr>
                              </thead>
                              <tbody>
                                {reports.recentActivity?.slice(0, 15).map((activity, index) => (
                                  <tr key={index}>
                                    <td>
                                      <strong>{activity.username}</strong>
                                    </td>
                                    <td>{activity.quizTitle}</td>
                                    <td>
                                      <span className="badge bg-primary">
                                        {activity.score}/{activity.totalQuestions}
                                      </span>
                                    </td>
                                    <td>
                                      <span className={`badge ${activity.percentage >= 80 ? 'bg-success' : activity.percentage >= 60 ? 'bg-warning' : 'bg-danger'}`}>
                                        {Number(activity.percentage).toFixed(1)}%
                                      </span>
                                    </td>
                                    <td>
                                      <span className="badge bg-secondary">
                                        {Math.floor(activity.timeTaken / 60)}m {activity.timeTaken % 60}s
                                      </span>
                                    </td>
                                    <td>
                                      {new Date(activity.completedAt).toLocaleString()}
                                    </td>
                                  </tr>
                                ))}
                              </tbody>
                            </table>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Create Quiz Form Component
const CreateQuizForm = ({ onSuccess, onCancel }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    durationMinutes: 30
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await adminAPI.createQuiz(formData);
      onSuccess();
    } catch (error) {
      setError('Error creating quiz');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Create New Quiz</h2>
        <button className="btn btn-secondary" onClick={onCancel}>
          <i className="fas fa-arrow-left me-2"></i>Back
        </button>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="title" className="form-label">Quiz Title *</label>
          <input
            type="text"
            className="form-control"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description</label>
          <textarea
            className="form-control"
            id="description"
            name="description"
            rows="3"
            value={formData.description}
            onChange={handleChange}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="durationMinutes" className="form-label">Duration (minutes) *</label>
          <input
            type="number"
            className="form-control"
            id="durationMinutes"
            name="durationMinutes"
            value={formData.durationMinutes}
            onChange={handleChange}
            min="1"
            required
          />
        </div>

        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
          </div>
        )}

        <div className="d-flex gap-2">
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? (
              <>
                <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                Creating...
              </>
            ) : (
              <>
                <i className="fas fa-save me-2"></i>Create Quiz
              </>
            )}
          </button>
          <button type="button" className="btn btn-secondary" onClick={onCancel}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

// Question Management Component
const QuestionManagement = ({ quizId, onBack }) => {
  const [questions, setQuestions] = useState([]);
  const [quiz, setQuiz] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [, setError] = useState('');

  const fetchQuestions = useCallback(async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getQuestionsByQuiz(quizId);
      setQuestions(response.data);
    } catch (error) {
      setError('Error fetching questions');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  }, [quizId]);

  const fetchQuiz = useCallback(async () => {
    try {
      const response = await adminAPI.getAllQuizzes();
      const currentQuiz = response.data.find(q => q.id === parseInt(quizId));
      setQuiz(currentQuiz);
    } catch (error) {
      setError('Error fetching quiz');
      console.error('Error:', error);
    }
  }, [quizId]);

  useEffect(() => {
    fetchQuestions();
    fetchQuiz();
  }, [fetchQuestions, fetchQuiz]);

  const handleDeleteQuestion = async (questionId) => {
    if (window.confirm('Are you sure you want to delete this question?')) {
      try {
        await adminAPI.deleteQuestion(questionId);
        setQuestions(questions.filter(q => q.id !== questionId));
      } catch (error) {
        setError('Error deleting question');
        console.error('Error:', error);
      }
    }
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h2>Questions for: {quiz?.title}</h2>
          <p className="text-muted">Total Questions: {questions.length}</p>
        </div>
        <div>
          <button 
            className="btn btn-primary me-2"
            onClick={() => setShowCreateForm(true)}
          >
            <i className="fas fa-plus me-2"></i>Add Question
          </button>
          <button className="btn btn-secondary" onClick={onBack}>
            <i className="fas fa-arrow-left me-2"></i>Back to Quizzes
          </button>
        </div>
      </div>

      {showCreateForm && (
        <CreateQuestionForm
          quizId={quizId}
          onSuccess={() => {
            setShowCreateForm(false);
            fetchQuestions();
          }}
          onCancel={() => setShowCreateForm(false)}
        />
      )}

      {loading ? (
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <div className="row">
          {questions.map((question, index) => (
            <div key={question.id} className="col-md-12 mb-3">
              <div className="card">
                <div className="card-header d-flex justify-content-between align-items-center">
                  <h6 className="mb-0">Question {index + 1}</h6>
                  <div className="btn-group">
                    <button 
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => handleDeleteQuestion(question.id)}
                    >
                      <i className="fas fa-trash"></i>
                    </button>
                  </div>
                </div>
                <div className="card-body">
                  <p className="card-text"><strong>Q:</strong> {question.questionText}</p>
                  <div className="row">
                    <div className="col-md-6">
                      <p><strong>A)</strong> {question.optionA}</p>
                      <p><strong>B)</strong> {question.optionB}</p>
                    </div>
                    <div className="col-md-6">
                      <p><strong>C)</strong> {question.optionC}</p>
                      <p><strong>D)</strong> {question.optionD}</p>
                    </div>
                  </div>
                  <p><strong>Correct Answer:</strong> <span className="badge bg-success">{question.correctAnswer}</span></p>
                  <p><strong>Marks:</strong> {question.marks}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// Create Question Form Component
const CreateQuestionForm = ({ quizId, onSuccess, onCancel }) => {
  const [formData, setFormData] = useState({
    questionText: '',
    optionA: '',
    optionB: '',
    optionC: '',
    optionD: '',
    correctAnswer: 'A',
    marks: 1,
    quizId: parseInt(quizId)
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await adminAPI.createQuestion(formData);
      onSuccess();
    } catch (error) {
      setError('Error creating question');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="card mb-4">
      <div className="card-header">
        <h5>Add New Question</h5>
      </div>
      <div className="card-body">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="questionText" className="form-label">Question Text *</label>
            <textarea
              className="form-control"
              id="questionText"
              name="questionText"
              rows="3"
              value={formData.questionText}
              onChange={handleChange}
              required
            />
          </div>

          <div className="row">
            <div className="col-md-6">
              <div className="mb-3">
                <label htmlFor="optionA" className="form-label">Option A *</label>
                <input
                  type="text"
                  className="form-control"
                  id="optionA"
                  name="optionA"
                  value={formData.optionA}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="mb-3">
                <label htmlFor="optionB" className="form-label">Option B *</label>
                <input
                  type="text"
                  className="form-control"
                  id="optionB"
                  name="optionB"
                  value={formData.optionB}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="row">
            <div className="col-md-6">
              <div className="mb-3">
                <label htmlFor="optionC" className="form-label">Option C *</label>
                <input
                  type="text"
                  className="form-control"
                  id="optionC"
                  name="optionC"
                  value={formData.optionC}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="mb-3">
                <label htmlFor="optionD" className="form-label">Option D *</label>
                <input
                  type="text"
                  className="form-control"
                  id="optionD"
                  name="optionD"
                  value={formData.optionD}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
          </div>

          <div className="row">
            <div className="col-md-6">
              <div className="mb-3">
                <label htmlFor="correctAnswer" className="form-label">Correct Answer *</label>
                <select
                  className="form-select"
                  id="correctAnswer"
                  name="correctAnswer"
                  value={formData.correctAnswer}
                  onChange={handleChange}
                  required
                >
                  <option value="A">A</option>
                  <option value="B">B</option>
                  <option value="C">C</option>
                  <option value="D">D</option>
                </select>
              </div>
            </div>
            <div className="col-md-6">
              <div className="mb-3">
                <label htmlFor="marks" className="form-label">Marks *</label>
                <input
                  type="number"
                  className="form-control"
                  id="marks"
                  name="marks"
                  value={formData.marks}
                  onChange={handleChange}
                  min="1"
                  required
                />
              </div>
            </div>
          </div>

          {error && (
            <div className="alert alert-danger" role="alert">
              {error}
            </div>
          )}

          <div className="d-flex gap-2">
            <button type="submit" className="btn btn-primary" disabled={loading}>
              {loading ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                  Adding...
                </>
              ) : (
                <>
                  <i className="fas fa-save me-2"></i>Add Question
                </>
              )}
            </button>
            <button type="button" className="btn btn-secondary" onClick={onCancel}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdminPanel;
