import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { quizAPI } from '../services/api';

const QuizPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  
  const [quiz, setQuiz] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [quizStarted, setQuizStarted] = useState(false);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchQuizData();
  }, [id]);

  useEffect(() => {
    let timer;
    if (quizStarted && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            handleSubmitQuiz();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [quizStarted, timeLeft]);

  const fetchQuizData = async () => {
    try {
      const [quizResponse, questionsResponse] = await Promise.all([
        quizAPI.getQuiz(id),
        quizAPI.getQuizQuestions(id)
      ]);
      
      setQuiz(quizResponse.data);
      setQuestions(questionsResponse.data);
      setTimeLeft(quizResponse.data.durationMinutes * 60);
    } catch (error) {
      setError('Error loading quiz');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const startQuiz = () => {
    setQuizStarted(true);
  };

  const handleAnswerSelect = (questionId, answer) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: answer
    }));
  };

  const handleSubmitQuiz = async () => {
    if (submitting) return;
    
    setSubmitting(true);
    const startTime = quiz.durationMinutes * 60;
    const timeTaken = startTime - timeLeft;

    try {
      const response = await quizAPI.submitQuiz({
        quizId: parseInt(id),
        answers,
        timeTakenSeconds: timeTaken
      });
      
      navigate(`/result/${response.data.id}`);
    } catch (error) {
      setError('Error submitting quiz');
      console.error('Error:', error);
      setSubmitting(false);
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const getTimeColor = () => {
    if (timeLeft <= 60) return 'text-danger timer-danger';
    if (timeLeft <= 300) return 'text-warning timer-warning';
    return 'text-success';
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '80vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger" role="alert">
          <i className="fas fa-exclamation-triangle me-2"></i>
          {error}
        </div>
      </div>
    );
  }

  // Quiz Start Screen
  if (!quizStarted) {
    return (
      <div className="container mt-4">
        <div className="row justify-content-center">
          <div className="col-md-8">
            <div className="card">
              <div className="card-body text-center p-5">
                <i className="fas fa-brain fa-4x text-primary mb-4"></i>
                <h1 className="card-title">{quiz?.title}</h1>
                <p className="card-text lead">{quiz?.description}</p>
                
                <div className="row mt-4">
                  <div className="col-md-4">
                    <div className="d-flex flex-column align-items-center">
                      <i className="fas fa-clock fa-2x text-info mb-2"></i>
                      <h5>Duration</h5>
                      <p>{quiz?.durationMinutes} minutes</p>
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="d-flex flex-column align-items-center">
                      <i className="fas fa-question-circle fa-2x text-success mb-2"></i>
                      <h5>Questions</h5>
                      <p>{questions.length} questions</p>
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="d-flex flex-column align-items-center">
                      <i className="fas fa-star fa-2x text-warning mb-2"></i>
                      <h5>Total Marks</h5>
                      <p>{quiz?.totalMarks || questions.length}</p>
                    </div>
                  </div>
                </div>

                <div className="alert alert-info mt-4">
                  <h6><i className="fas fa-info-circle me-2"></i>Instructions:</h6>
                  <ul className="text-start mb-0">
                    <li>Read each question carefully</li>
                    <li>Select the best answer for each question</li>
                    <li>You can navigate between questions</li>
                    <li>The quiz will auto-submit when time is up</li>
                    <li>Make sure you have a stable internet connection</li>
                  </ul>
                </div>

                <button 
                  className="btn btn-primary btn-lg mt-4"
                  onClick={startQuiz}
                >
                  <i className="fas fa-play me-2"></i>
                  Start Quiz
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="container-fluid mt-3">
      {/* Timer and Progress */}
      <div className="row mb-3">
        <div className="col-12">
          <div className="card">
            <div className="card-body py-2">
              <div className="d-flex justify-content-between align-items-center">
                <div>
                  <h6 className="mb-0">{quiz?.title}</h6>
                  <small className="text-muted">
                    Question {currentQuestionIndex + 1} of {questions.length}
                  </small>
                </div>
                <div className="text-center">
                  <div className="progress" style={{ width: '200px', height: '8px' }}>
                    <div 
                      className="progress-bar" 
                      style={{ width: `${((currentQuestionIndex + 1) / questions.length) * 100}%` }}
                    ></div>
                  </div>
                </div>
                <div className="text-end">
                  <h4 className={`mb-0 ${getTimeColor()}`}>
                    <i className="fas fa-clock me-2"></i>
                    {formatTime(timeLeft)}
                  </h4>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        {/* Question */}
        <div className="col-md-8">
          <div className="card question-card">
            <div className="card-body">
              <h5 className="card-title">
                Question {currentQuestionIndex + 1}
              </h5>
              <p className="card-text lead">{currentQuestion?.questionText}</p>
              
              <div className="mt-4">
                {['A', 'B', 'C', 'D'].map((option) => (
                  <div 
                    key={option}
                    className={`card option-card mb-2 ${answers[currentQuestion?.id] === option ? 'selected' : ''}`}
                    onClick={() => handleAnswerSelect(currentQuestion?.id, option)}
                  >
                    <div className="card-body py-3">
                      <div className="form-check">
                        <input
                          className="form-check-input"
                          type="radio"
                          name={`question-${currentQuestion?.id}`}
                          id={`${currentQuestion?.id}-${option}`}
                          checked={answers[currentQuestion?.id] === option}
                          onChange={() => handleAnswerSelect(currentQuestion?.id, option)}
                        />
                        <label 
                          className="form-check-label w-100" 
                          htmlFor={`${currentQuestion?.id}-${option}`}
                        >
                          <strong>{option})</strong> {currentQuestion?.[`option${option}`]}
                        </label>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Navigation */}
          <div className="card mt-3">
            <div className="card-body">
              <div className="d-flex justify-content-between">
                <button
                  className="btn btn-outline-secondary"
                  onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))}
                  disabled={currentQuestionIndex === 0}
                >
                  <i className="fas fa-chevron-left me-2"></i>
                  Previous
                </button>
                
                <div className="d-flex gap-2">
                  {currentQuestionIndex === questions.length - 1 ? (
                    <button
                      className="btn btn-success"
                      onClick={handleSubmitQuiz}
                      disabled={submitting}
                    >
                      {submitting ? (
                        <>
                          <span className="spinner-border spinner-border-sm me-2"></span>
                          Submitting...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-check me-2"></i>
                          Submit Quiz
                        </>
                      )}
                    </button>
                  ) : (
                    <button
                      className="btn btn-primary"
                      onClick={() => setCurrentQuestionIndex(Math.min(questions.length - 1, currentQuestionIndex + 1))}
                    >
                      Next
                      <i className="fas fa-chevron-right ms-2"></i>
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Question Navigator */}
        <div className="col-md-4">
          <div className="card">
            <div className="card-header">
              <h6 className="mb-0">Question Navigator</h6>
            </div>
            <div className="card-body">
              <div className="d-flex flex-wrap gap-2">
                {questions.map((question, index) => (
                  <button
                    key={question.id}
                    className={`question-nav-btn btn btn-sm ${
                      index === currentQuestionIndex 
                        ? 'current' 
                        : answers[question.id] 
                        ? 'answered' 
                        : 'btn-outline-secondary'
                    }`}
                    onClick={() => setCurrentQuestionIndex(index)}
                  >
                    {index + 1}
                  </button>
                ))}
              </div>
              
              <div className="mt-3">
                <small className="text-muted">
                  <div className="d-flex align-items-center mb-1">
                    <div className="question-nav-btn btn btn-sm answered me-2" style={{width: '20px', height: '20px'}}></div>
                    Answered ({Object.keys(answers).length})
                  </div>
                  <div className="d-flex align-items-center mb-1">
                    <div className="question-nav-btn btn btn-sm current me-2" style={{width: '20px', height: '20px'}}></div>
                    Current
                  </div>
                  <div className="d-flex align-items-center">
                    <div className="question-nav-btn btn btn-sm btn-outline-secondary me-2" style={{width: '20px', height: '20px'}}></div>
                    Not Answered ({questions.length - Object.keys(answers).length})
                  </div>
                </small>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuizPage;
