import React, { useState } from 'react';
import { adminAPI } from '../services/api';

const QuizCreationForm = ({ onQuizCreated, onClose, editingQuiz }) => {
  const [formData, setFormData] = useState({
    title: editingQuiz?.title || '',
    description: editingQuiz?.description || '',
    durationMinutes: editingQuiz?.durationMinutes || 30,
    isActive: editingQuiz?.isActive !== undefined ? editingQuiz.isActive : true
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Quiz title is required';
    } else if (formData.title.length < 3) {
      newErrors.title = 'Quiz title must be at least 3 characters';
    }

    if (!formData.description.trim()) {
      newErrors.description = 'Quiz description is required';
    } else if (formData.description.length < 10) {
      newErrors.description = 'Quiz description must be at least 10 characters';
    }

    if (!formData.durationMinutes || formData.durationMinutes < 1) {
      newErrors.durationMinutes = 'Duration must be at least 1 minute';
    } else if (formData.durationMinutes > 300) {
      newErrors.durationMinutes = 'Duration cannot exceed 300 minutes (5 hours)';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      setLoading(true);
      
      const quizData = {
        ...formData,
        durationMinutes: parseInt(formData.durationMinutes)
      };

      let response;
      if (editingQuiz) {
        response = await adminAPI.updateQuiz(editingQuiz.id, quizData);
      } else {
        response = await adminAPI.createQuiz(quizData);
      }

      // Reset form
      setFormData({
        title: '',
        description: '',
        durationMinutes: 30,
        isActive: true
      });

      if (onQuizCreated) {
        onQuizCreated(response.data);
      }

      if (onClose) {
        onClose();
      }

    } catch (error) {
      console.error(`Error ${editingQuiz ? 'updating' : 'creating'} quiz:`, error);
      setErrors({ submit: `Error ${editingQuiz ? 'updating' : 'creating'} quiz. Please try again.` });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="quiz-card">
      <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white px-6 py-4 rounded-t-xl">
        <h3 className="text-xl font-semibold flex items-center">
          <i className={`fas ${editingQuiz ? 'fa-edit' : 'fa-plus-circle'} mr-2`}></i>
          {editingQuiz ? 'Edit Quiz' : 'Create New Quiz'}
        </h3>
      </div>

      <div className="p-6">
        <form onSubmit={handleSubmit}>
          {/* Quiz Title */}
          <div className="mb-6">
            <label className="form-label text-lg font-semibold text-gray-700">
              <i className="fas fa-heading mr-2"></i>
              Quiz Title *
            </label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className={`form-control ${errors.title ? 'border-red-500' : ''}`}
              placeholder="Enter a compelling quiz title..."
              maxLength="100"
            />
            <div className="flex justify-between mt-1">
              {errors.title && (
                <div className="text-red-500 text-sm">
                  <i className="fas fa-exclamation-circle mr-1"></i>
                  {errors.title}
                </div>
              )}
              <div className="text-gray-400 text-sm ml-auto">
                {formData.title.length}/100
              </div>
            </div>
          </div>

          {/* Quiz Description */}
          <div className="mb-6">
            <label className="form-label text-lg font-semibold text-gray-700">
              <i className="fas fa-align-left mr-2"></i>
              Quiz Description *
            </label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className={`form-control ${errors.description ? 'border-red-500' : ''}`}
              rows="4"
              placeholder="Describe what this quiz is about, topics covered, difficulty level..."
              maxLength="500"
            />
            <div className="flex justify-between mt-1">
              {errors.description && (
                <div className="text-red-500 text-sm">
                  <i className="fas fa-exclamation-circle mr-1"></i>
                  {errors.description}
                </div>
              )}
              <div className="text-gray-400 text-sm ml-auto">
                {formData.description.length}/500
              </div>
            </div>
          </div>

          {/* Duration and Settings */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            {/* Duration */}
            <div>
              <label className="form-label text-lg font-semibold text-gray-700">
                <i className="fas fa-clock mr-2"></i>
                Duration (minutes) *
              </label>
              <input
                type="number"
                name="durationMinutes"
                value={formData.durationMinutes}
                onChange={handleChange}
                className={`form-control ${errors.durationMinutes ? 'border-red-500' : ''}`}
                min="1"
                max="300"
                placeholder="30"
              />
              {errors.durationMinutes && (
                <div className="text-red-500 text-sm mt-1">
                  <i className="fas fa-exclamation-circle mr-1"></i>
                  {errors.durationMinutes}
                </div>
              )}
              <div className="text-gray-500 text-sm mt-1">
                Recommended: 30-60 minutes for most quizzes
              </div>
            </div>

            {/* Active Status */}
            <div>
              <label className="form-label text-lg font-semibold text-gray-700">
                <i className="fas fa-toggle-on mr-2"></i>
                Quiz Status
              </label>
              <div className="flex items-center space-x-3 mt-2">
                <label className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="checkbox"
                    name="isActive"
                    checked={formData.isActive}
                    onChange={handleChange}
                    className="form-check-input"
                  />
                  <span className={`font-medium ${formData.isActive ? 'text-green-600' : 'text-gray-600'}`}>
                    {formData.isActive ? 'Active (visible to users)' : 'Inactive (hidden from users)'}
                  </span>
                </label>
              </div>
              <div className="text-gray-500 text-sm mt-1">
                {formData.isActive 
                  ? 'Users can see and take this quiz' 
                  : 'Quiz will be hidden until activated'
                }
              </div>
            </div>
          </div>

          {/* Quiz Preview */}
          <div className="mb-6 p-4 bg-gray-50 rounded-lg border-l-4 border-blue-500">
            <h4 className="font-semibold text-gray-800 mb-2">
              <i className="fas fa-eye mr-2"></i>
              Quiz Preview
            </h4>
            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-600">Title:</span>
                <span className="font-medium">{formData.title || 'Untitled Quiz'}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Duration:</span>
                <span className="font-medium">{formData.durationMinutes} minutes</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Status:</span>
                <span className={`font-medium ${formData.isActive ? 'text-green-600' : 'text-gray-600'}`}>
                  {formData.isActive ? 'Active' : 'Inactive'}
                </span>
              </div>
              <div className="pt-2 border-t">
                <span className="text-gray-600">Description:</span>
                <p className="text-gray-800 mt-1">{formData.description || 'No description provided'}</p>
              </div>
            </div>
          </div>

          {/* Next Steps Info */}
          <div className="mb-6 p-4 bg-blue-50 rounded-lg border border-blue-200">
            <h4 className="font-semibold text-blue-800 mb-2">
              <i className="fas fa-info-circle mr-2"></i>
              What happens next?
            </h4>
            <ul className="text-blue-700 text-sm space-y-1">
              <li>• Quiz will be created with 0 questions and 0 total marks</li>
              <li>• You can add questions to set the total marks and question count</li>
              <li>• Each question can have custom marks (default: 1 mark)</li>
              <li>• Total marks will be calculated automatically based on all questions</li>
            </ul>
          </div>

          {/* Error Message */}
          {errors.submit && (
            <div className="mb-6 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
              <i className="fas fa-exclamation-triangle mr-2"></i>
              {errors.submit}
            </div>
          )}

          {/* Buttons */}
          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="btn-outline"
              disabled={loading}
            >
              <i className="fas fa-times mr-2"></i>
              Cancel
            </button>
            <button
              type="submit"
              className="btn-primary"
              disabled={loading}
            >
              {loading ? (
                <>
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Creating Quiz...
                </>
              ) : (
                <>
                  <i className="fas fa-plus mr-2"></i>
                  Create Quiz
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default QuizCreationForm;
