import React, { useState, useEffect } from 'react';
import { adminAPI } from '../services/api';

const QuestionForm = ({ quizId, onQuestionAdded, onClose, existingQuestion = null }) => {
  const [formData, setFormData] = useState({
    questionText: '',
    optionA: '',
    optionB: '',
    optionC: '',
    optionD: '',
    correctAnswer: '',
    marks: 1,
    quizId: quizId
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (existingQuestion) {
      setFormData({
        questionText: existingQuestion.questionText || '',
        optionA: existingQuestion.optionA || '',
        optionB: existingQuestion.optionB || '',
        optionC: existingQuestion.optionC || '',
        optionD: existingQuestion.optionD || '',
        correctAnswer: existingQuestion.correctAnswer || '',
        marks: existingQuestion.marks || 1,
        quizId: quizId
      });
    }
  }, [existingQuestion, quizId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.questionText.trim()) {
      newErrors.questionText = 'Question text is required';
    }
    
    if (!formData.optionA.trim()) {
      newErrors.optionA = 'Option A is required';
    }
    
    if (!formData.optionB.trim()) {
      newErrors.optionB = 'Option B is required';
    }
    
    if (!formData.optionC.trim()) {
      newErrors.optionC = 'Option C is required';
    }
    
    if (!formData.optionD.trim()) {
      newErrors.optionD = 'Option D is required';
    }
    
    if (!formData.correctAnswer) {
      newErrors.correctAnswer = 'Please select the correct answer';
    }
    
    if (!formData.marks || formData.marks < 1) {
      newErrors.marks = 'Marks must be at least 1';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    try {
      setLoading(true);
      
      if (existingQuestion) {
        await adminAPI.updateQuestion(existingQuestion.id, formData);
      } else {
        await adminAPI.createQuestion(formData);
      }
      
      // Reset form
      setFormData({
        questionText: '',
        optionA: '',
        optionB: '',
        optionC: '',
        optionD: '',
        correctAnswer: '',
        marks: 1,
        quizId: quizId
      });
      
      if (onQuestionAdded) {
        onQuestionAdded();
      }
      
      if (onClose) {
        onClose();
      }
      
    } catch (error) {
      console.error('Error saving question:', error);
      setErrors({ submit: 'Error saving question. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white rounded-2xl shadow-xl overflow-hidden max-w-4xl mx-auto">
      {/* Simple Header */}
      <div className="bg-gradient-to-r from-green-500 to-blue-500 text-white px-6 py-4">
        <div className="flex items-center">
          <div className="bg-white/20 rounded-lg p-2 mr-3">
            <i className="fas fa-question-circle text-lg"></i>
          </div>
          <h2 className="text-xl font-bold">
            {existingQuestion ? 'Edit Question' : 'Add Question'}
          </h2>
        </div>
      </div>
      
      <div className="p-6">
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Question Text - Simple */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Question
            </label>
            <textarea
              name="questionText"
              value={formData.questionText}
              onChange={handleChange}
              className={`w-full px-3 py-3 text-base border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none ${
                errors.questionText 
                  ? 'border-red-400' 
                  : 'border-gray-300'
              }`}
              rows="3"
              placeholder="Type your question here..."
            />
            {errors.questionText && (
              <p className="text-red-500 text-sm mt-1">{errors.questionText}</p>
            )}
          </div>

          {/* Answer Options - Simple Grid */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">
              Answer Options (click to select correct answer)
            </label>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Option A */}
              <div>
                <div className="flex items-center mb-2">
                  <button
                    type="button"
                    onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'A' }))}
                    className={`w-8 h-8 rounded-lg flex items-center justify-center font-bold text-sm mr-2 transition-all ${
                      formData.correctAnswer === 'A'
                        ? 'bg-green-500 text-white'
                        : 'bg-gray-200 text-gray-600 hover:bg-green-100'
                    }`}
                  >
                    A
                  </button>
                  <span className="text-sm font-medium text-gray-700">Option A</span>
                  {formData.correctAnswer === 'A' && (
                    <i className="fas fa-check text-green-500 ml-2"></i>
                  )}
                </div>
                <input
                  type="text"
                  name="optionA"
                  value={formData.optionA}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 ${
                    formData.correctAnswer === 'A'
                      ? 'border-green-400 bg-green-50'
                      : errors.optionA
                      ? 'border-red-400'
                      : 'border-gray-300'
                  }`}
                  placeholder="Enter option A"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'A' }))}
                />
                {errors.optionA && (
                  <p className="text-red-500 text-sm mt-1">{errors.optionA}</p>
                )}
              </div>

              {/* Option B */}
              <div>
                <div className="flex items-center mb-2">
                  <button
                    type="button"
                    onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'B' }))}
                    className={`w-8 h-8 rounded-lg flex items-center justify-center font-bold text-sm mr-2 transition-all ${
                      formData.correctAnswer === 'B'
                        ? 'bg-green-500 text-white'
                        : 'bg-gray-200 text-gray-600 hover:bg-green-100'
                    }`}
                  >
                    B
                  </button>
                  <span className="text-sm font-medium text-gray-700">Option B</span>
                  {formData.correctAnswer === 'B' && (
                    <i className="fas fa-check text-green-500 ml-2"></i>
                  )}
                </div>
                <input
                  type="text"
                  name="optionB"
                  value={formData.optionB}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 ${
                    formData.correctAnswer === 'B'
                      ? 'border-green-400 bg-green-50'
                      : errors.optionB
                      ? 'border-red-400'
                      : 'border-gray-300'
                  }`}
                  placeholder="Enter option B"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'B' }))}
                />
                {errors.optionB && (
                  <p className="text-red-500 text-sm mt-1">{errors.optionB}</p>
                )}
              </div>

              {/* Option C */}
              <div>
                <div className="flex items-center mb-2">
                  <button
                    type="button"
                    onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'C' }))}
                    className={`w-8 h-8 rounded-lg flex items-center justify-center font-bold text-sm mr-2 transition-all ${
                      formData.correctAnswer === 'C'
                        ? 'bg-green-500 text-white'
                        : 'bg-gray-200 text-gray-600 hover:bg-green-100'
                    }`}
                  >
                    C
                  </button>
                  <span className="text-sm font-medium text-gray-700">Option C</span>
                  {formData.correctAnswer === 'C' && (
                    <i className="fas fa-check text-green-500 ml-2"></i>
                  )}
                </div>
                <input
                  type="text"
                  name="optionC"
                  value={formData.optionC}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 ${
                    formData.correctAnswer === 'C'
                      ? 'border-green-400 bg-green-50'
                      : errors.optionC
                      ? 'border-red-400'
                      : 'border-gray-300'
                  }`}
                  placeholder="Enter option C"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'C' }))}
                />
                {errors.optionC && (
                  <p className="text-red-500 text-sm mt-1">{errors.optionC}</p>
                )}
              </div>

              {/* Option D */}
              <div>
                <div className="flex items-center mb-2">
                  <button
                    type="button"
                    onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'D' }))}
                    className={`w-8 h-8 rounded-lg flex items-center justify-center font-bold text-sm mr-2 transition-all ${
                      formData.correctAnswer === 'D'
                        ? 'bg-green-500 text-white'
                        : 'bg-gray-200 text-gray-600 hover:bg-green-100'
                    }`}
                  >
                    D
                  </button>
                  <span className="text-sm font-medium text-gray-700">Option D</span>
                  {formData.correctAnswer === 'D' && (
                    <i className="fas fa-check text-green-500 ml-2"></i>
                  )}
                </div>
                <input
                  type="text"
                  name="optionD"
                  value={formData.optionD}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 ${
                    formData.correctAnswer === 'D'
                      ? 'border-green-400 bg-green-50'
                      : errors.optionD
                      ? 'border-red-400'
                      : 'border-gray-300'
                  }`}
                  placeholder="Enter option D"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'D' }))}
                />
                {errors.optionD && (
                  <p className="text-red-500 text-sm mt-1">{errors.optionD}</p>
                )}
              </div>
            </div>
            
            {errors.correctAnswer && (
              <p className="text-red-500 text-sm mt-2">{errors.correctAnswer}</p>
            )}
          </div>

          {/* Points - Simple */}
          <div className="w-32">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Points
            </label>
            <input
              type="number"
              name="marks"
              value={formData.marks}
              onChange={handleChange}
              className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                errors.marks ? 'border-red-400' : 'border-gray-300'
              }`}
              min="1"
              max="10"
            />
            {errors.marks && (
              <p className="text-red-500 text-sm mt-1">{errors.marks}</p>
            )}
          </div>

          {/* Correct Answer Preview - Simple */}
          {formData.correctAnswer && formData[`option${formData.correctAnswer}`] && (
            <div className="bg-green-50 border border-green-200 rounded-lg p-3">
              <p className="text-green-800 text-sm">
                <strong>Correct Answer:</strong> {formData.correctAnswer}. {formData[`option${formData.correctAnswer}`]}
              </p>
            </div>
          )}

          {/* Error Message */}
          {errors.submit && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-3">
              <p className="text-red-700 text-sm">{errors.submit}</p>
            </div>
          )}

          {/* Simple Buttons */}
          <div className="flex justify-end space-x-3 pt-4 border-t">
            <button
              type="button"
              onClick={onClose}
              disabled={loading}
              className="px-4 py-2 text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500 disabled:opacity-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              className="px-4 py-2 text-white bg-green-600 rounded-lg hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 disabled:opacity-50"
            >
              {loading ? (
                <>
                  <div className="animate-spin inline-block w-4 h-4 border-2 border-current border-t-transparent rounded-full mr-2"></div>
                  Saving...
                </>
              ) : (
                <>
                  {existingQuestion ? 'Update Question' : 'Add Question'}
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default QuestionForm;
