import React, { useState } from 'react';
import { adminAPI } from '../services/api';

const QuizCreationForm = ({ onQuizCreated, onClose, editingQuiz }) => {
  const [formData, setFormData] = useState({
    title: editingQuiz?.title || '',
    description: editingQuiz?.description || '',
    durationMinutes: editingQuiz?.durationMinutes || 30,
    isActive: editingQuiz?.isActive !== undefined ? editingQuiz.isActive : true
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Quiz title is required';
    } else if (formData.title.length < 3) {
      newErrors.title = 'Quiz title must be at least 3 characters';
    }

    if (!formData.description.trim()) {
      newErrors.description = 'Quiz description is required';
    } else if (formData.description.length < 10) {
      newErrors.description = 'Quiz description must be at least 10 characters';
    }

    if (!formData.durationMinutes || formData.durationMinutes < 1) {
      newErrors.durationMinutes = 'Duration must be at least 1 minute';
    } else if (formData.durationMinutes > 300) {
      newErrors.durationMinutes = 'Duration cannot exceed 300 minutes (5 hours)';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      setLoading(true);
      
      const quizData = {
        ...formData,
        durationMinutes: parseInt(formData.durationMinutes)
      };

      let response;
      if (editingQuiz) {
        response = await adminAPI.updateQuiz(editingQuiz.id, quizData);
      } else {
        response = await adminAPI.createQuiz(quizData);
      }

      // Reset form
      setFormData({
        title: '',
        description: '',
        durationMinutes: 30,
        isActive: true
      });

      if (onQuizCreated) {
        onQuizCreated(response.data);
      }

      if (onClose) {
        onClose();
      }

    } catch (error) {
      console.error(`Error ${editingQuiz ? 'updating' : 'creating'} quiz:`, error);
      setErrors({ submit: `Error ${editingQuiz ? 'updating' : 'creating'} quiz. Please try again.` });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white rounded-2xl shadow-xl overflow-hidden max-w-2xl mx-auto">
      {/* Simple Header */}
      <div className="bg-gradient-to-r from-blue-500 to-purple-500 text-white px-6 py-4">
        <div className="flex items-center">
          <div className="bg-white/20 rounded-lg p-2 mr-3">
            <i className={`fas ${editingQuiz ? 'fa-edit' : 'fa-plus'} text-lg`}></i>
          </div>
          <h2 className="text-xl font-bold">
            {editingQuiz ? 'Edit Quiz' : 'Create New Quiz'}
          </h2>
        </div>
      </div>

      <div className="p-6">
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Quiz Title - Simple */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Quiz Title
            </label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                errors.title ? 'border-red-400' : 'border-gray-300'
              }`}
              placeholder="Enter quiz title"
              maxLength="100"
            />
            {errors.title && (
              <p className="text-red-500 text-sm mt-1">{errors.title}</p>
            )}
          </div>

          {/* Quiz Description - Simple */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Description
            </label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none ${
                errors.description ? 'border-red-400' : 'border-gray-300'
              }`}
              rows="3"
              placeholder="Describe what this quiz is about"
              maxLength="500"
            />
            {errors.description && (
              <p className="text-red-500 text-sm mt-1">{errors.description}</p>
            )}
          </div>

          {/* Duration and Status - Simple Row */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Duration (minutes)
              </label>
              <input
                type="number"
                name="durationMinutes"
                value={formData.durationMinutes}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.durationMinutes ? 'border-red-400' : 'border-gray-300'
                }`}
                min="1"
                max="300"
                placeholder="30"
              />
              {errors.durationMinutes && (
                <p className="text-red-500 text-sm mt-1">{errors.durationMinutes}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Status
              </label>
              <div className="flex items-center h-10">
                <label className="flex items-center cursor-pointer">
                  <input
                    type="checkbox"
                    name="isActive"
                    checked={formData.isActive}
                    onChange={handleChange}
                    className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                  />
                  <span className="ml-2 text-sm text-gray-700">
                    Active (visible to users)
                  </span>
                </label>
              </div>
            </div>
          </div>

          {/* Simple Preview Box */}
          {(formData.title || formData.description) && (
            <div className="bg-gray-50 rounded-lg p-4 border">
              <h4 className="text-sm font-medium text-gray-700 mb-2">Preview</h4>
              <div className="space-y-1 text-sm">
                {formData.title && (
                  <p><span className="font-medium">Title:</span> {formData.title}</p>
                )}
                {formData.description && (
                  <p><span className="font-medium">Description:</span> {formData.description}</p>
                )}
                <p><span className="font-medium">Duration:</span> {formData.durationMinutes} minutes</p>
                <p><span className="font-medium">Status:</span> 
                  <span className={formData.isActive ? 'text-green-600' : 'text-gray-600'}>
                    {formData.isActive ? ' Active' : ' Inactive'}
                  </span>
                </p>
              </div>
            </div>
          )}

          {/* Error Message */}
          {errors.submit && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-3">
              <p className="text-red-700 text-sm">{errors.submit}</p>
            </div>
          )}

          {/* Simple Buttons */}
          <div className="flex justify-end space-x-3 pt-4 border-t">
            <button
              type="button"
              onClick={onClose}
              disabled={loading}
              className="px-4 py-2 text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500 disabled:opacity-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              className="px-4 py-2 text-white bg-blue-600 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {loading ? (
                <>
                  <div className="animate-spin inline-block w-4 h-4 border-2 border-current border-t-transparent rounded-full mr-2"></div>
                  {editingQuiz ? 'Updating...' : 'Creating...'}
                </>
              ) : (
                <>
                  {editingQuiz ? 'Update Quiz' : 'Create Quiz'}
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default QuizCreationForm;
