import React, { useState, useEffect } from 'react';
import { adminAPI } from '../services/api';
import QuestionForm from '../components/QuestionForm';
import QuizSummary from './QuizSummary';
import QuizCreationForm from './QuizCreationForm';

const QuizManagement = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [showQuestionForm, setShowQuestionForm] = useState(false);
  const [editingQuestion, setEditingQuestion] = useState(null);
  const [showQuizForm, setShowQuizForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  const [quizFormData, setQuizFormData] = useState({
    title: '',
    description: '',
    durationMinutes: 30
  });

  useEffect(() => {
    fetchQuizzes();
  }, []);

  useEffect(() => {
    if (selectedQuiz) {
      fetchQuestions(selectedQuiz.id);
    }
  }, [selectedQuiz]);

  const fetchQuizzes = async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getAllQuizzes();
      setQuizzes(response.data);
    } catch (error) {
      setError('Error fetching quizzes');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchQuestions = async (quizId) => {
    try {
      const response = await adminAPI.getQuestionsByQuiz(quizId);
      setQuestions(response.data);
    } catch (error) {
      setError('Error fetching questions');
      console.error('Error:', error);
    }
  };

  const handleCreateQuiz = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const response = await adminAPI.createQuiz(quizFormData);
      setQuizzes([...quizzes, response.data]);
      setQuizFormData({ title: '', description: '', durationMinutes: 30 });
      setShowQuizForm(false);
    } catch (error) {
      setError('Error creating quiz');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEditQuiz = (quiz) => {
    setQuizFormData({
      title: quiz.title,
      description: quiz.description,
      durationMinutes: quiz.durationMinutes
    });
    setSelectedQuiz(quiz);
    setShowQuizForm(true);
  };

  const handleDeleteQuiz = async (quizId) => {
    if (window.confirm('Are you sure you want to delete this quiz? This will also delete all questions in it.')) {
      try {
        await adminAPI.deleteQuiz(quizId);
        setQuizzes(quizzes.filter(quiz => quiz.id !== quizId));
        if (selectedQuiz && selectedQuiz.id === quizId) {
          setSelectedQuiz(null);
          setQuestions([]);
        }
      } catch (error) {
        setError('Error deleting quiz');
        console.error('Error:', error);
      }
    }
  };

  const handleDeleteQuestion = async (questionId) => {
    if (window.confirm('Are you sure you want to delete this question?')) {
      try {
        await adminAPI.deleteQuestion(questionId);
        setQuestions(questions.filter(q => q.id !== questionId));
        // Update quiz total marks
        if (selectedQuiz) {
          fetchQuizzes();
        }
      } catch (error) {
        setError('Error deleting question');
        console.error('Error:', error);
      }
    }
  };

  const handleQuestionAdded = () => {
    if (selectedQuiz) {
      fetchQuestions(selectedQuiz.id);
      fetchQuizzes(); // Refresh to update total marks
    }
    setShowQuestionForm(false);
    setEditingQuestion(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 px-4 py-8">
      <div className="max-w-7xl mx-auto">
        {/* Enhanced Header */}
        <div className="bg-white rounded-2xl shadow-xl border border-slate-200 mb-8 overflow-hidden">
          <div className="bg-gradient-to-r from-indigo-600 via-purple-600 to-blue-600 px-8 py-6">
            <div className="flex justify-between items-center">
              <div>
                <h1 className="text-3xl font-bold text-white flex items-center">
                  <div className="bg-white/20 backdrop-blur-sm rounded-xl p-3 mr-4">
                    <i className="fas fa-tasks text-2xl"></i>
                  </div>
                  Quiz & Question Management
                </h1>
                <p className="text-indigo-100 mt-2 text-lg">Create, edit, and manage your quiz questions with ease</p>
              </div>
              <button
                onClick={() => {
                  setSelectedQuiz(null);
                  setShowQuizForm(true);
                }}
                className="bg-white/20 backdrop-blur-sm text-white px-6 py-3 rounded-xl hover:bg-white/30 transition-all duration-300 flex items-center shadow-lg hover:shadow-xl transform hover:-translate-y-1"
              >
                <i className="fas fa-plus mr-2"></i>
                Create New Quiz
              </button>
            </div>
          </div>
          
          {/* Stats Dashboard */}
          <div className="p-6 bg-gradient-to-r from-slate-50 to-blue-50">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white rounded-xl p-4 shadow-md border border-slate-200">
                <div className="flex items-center">
                  <div className="bg-blue-500 rounded-xl p-3 mr-4">
                    <i className="fas fa-list-alt text-white text-xl"></i>
                  </div>
                  <div>
                    <div className="text-2xl font-bold text-gray-800">{quizzes.length}</div>
                    <div className="text-sm text-gray-600">Total Quizzes</div>
                  </div>
                </div>
              </div>
              <div className="bg-white rounded-xl p-4 shadow-md border border-slate-200">
                <div className="flex items-center">
                  <div className="bg-green-500 rounded-xl p-3 mr-4">
                    <i className="fas fa-question-circle text-white text-xl"></i>
                  </div>
                  <div>
                    <div className="text-2xl font-bold text-gray-800">{questions.length}</div>
                    <div className="text-sm text-gray-600">Questions in Selected Quiz</div>
                  </div>
                </div>
              </div>
              <div className="bg-white rounded-xl p-4 shadow-md border border-slate-200">
                <div className="flex items-center">
                  <div className="bg-purple-500 rounded-xl p-3 mr-4">
                    <i className="fas fa-star text-white text-xl"></i>
                  </div>
                  <div>
                    <div className="text-2xl font-bold text-gray-800">{selectedQuiz?.totalMarks || 0}</div>
                    <div className="text-sm text-gray-600">Total Marks</div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {error && (
          <div className="bg-red-50 border-l-4 border-red-400 p-4 mb-6 rounded-r-xl shadow-lg">
            <div className="flex items-center">
              <div className="bg-red-100 rounded-full p-2 mr-3">
                <i className="fas fa-exclamation-triangle text-red-500"></i>
              </div>
              <div>
                <h4 className="text-red-800 font-medium">Error</h4>
                <p className="text-red-700">{error}</p>
              </div>
            </div>
          </div>
        )}

        <div className="grid grid-cols-1 xl:grid-cols-3 gap-8">
          {/* Enhanced Quiz List */}
          <div className="xl:col-span-1">
            <div className="bg-white rounded-2xl shadow-xl border border-slate-200 overflow-hidden">
              <div className="bg-gradient-to-r from-blue-500 to-cyan-500 text-white px-6 py-4">
                <h3 className="text-xl font-bold flex items-center">
                  <div className="bg-white/20 backdrop-blur-sm rounded-lg p-2 mr-3">
                    <i className="fas fa-list"></i>
                  </div>
                  Available Quizzes ({quizzes.length})
                </h3>
              </div>
              <div className="p-4 max-h-96 overflow-y-auto">
                {loading ? (
                  <div className="text-center py-8">
                    <div className="bg-gradient-to-r from-blue-500 to-purple-500 rounded-full w-12 h-12 mx-auto mb-4 flex items-center justify-center">
                      <i className="fas fa-spinner fa-spin text-white text-xl"></i>
                    </div>
                    <p className="text-gray-500">Loading quizzes...</p>
                  </div>
                ) : quizzes.length === 0 ? (
                  <div className="text-center py-12">
                    <div className="bg-gray-100 rounded-full w-16 h-16 mx-auto mb-4 flex items-center justify-center">
                      <i className="fas fa-inbox text-gray-400 text-2xl"></i>
                    </div>
                    <p className="text-gray-500 text-lg mb-2">No quizzes created yet</p>
                    <p className="text-gray-400 text-sm">Click "Create New Quiz" to get started</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {quizzes.map((quiz) => (
                      <QuizSummary
                        key={quiz.id}
                        quiz={quiz}
                        onEdit={() => handleEditQuiz(quiz)}
                        onAddQuestion={() => {
                          setSelectedQuiz(quiz);
                          setShowQuestionForm(true);
                        }}
                        onDelete={handleDeleteQuiz}
                      />
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Enhanced Questions Management */}
          <div className="xl:col-span-2">
            {selectedQuiz ? (
              <div className="space-y-6">
                {/* Enhanced Quiz Header */}
                <div className="bg-white rounded-2xl shadow-xl border border-slate-200 overflow-hidden">
                  <div className="bg-gradient-to-r from-emerald-500 to-teal-500 text-white px-6 py-6">
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <div className="flex items-center mb-3">
                          <div className="bg-white/20 backdrop-blur-sm rounded-xl p-3 mr-4">
                            <i className="fas fa-clipboard-list text-2xl"></i>
                          </div>
                          <div>
                            <h3 className="text-2xl font-bold">{selectedQuiz.title}</h3>
                            <p className="text-emerald-100 mt-1">{selectedQuiz.description}</p>
                          </div>
                        </div>
                      </div>
                      <button
                        onClick={() => setShowQuestionForm(true)}
                        className="bg-white/20 backdrop-blur-sm text-white px-6 py-3 rounded-xl hover:bg-white/30 transition-all duration-300 flex items-center shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                      >
                        <i className="fas fa-plus mr-2"></i>
                        Add Question
                      </button>
                    </div>
                  </div>
                  <div className="p-6 bg-gradient-to-r from-slate-50 to-emerald-50">
                    <div className="grid grid-cols-3 gap-6">
                      <div className="text-center">
                        <div className="bg-white rounded-xl p-4 shadow-md border border-slate-200">
                          <div className="text-3xl font-bold text-emerald-600 mb-1">{questions.length}</div>
                          <div className="text-sm text-gray-600 font-medium">Questions</div>
                        </div>
                      </div>
                      <div className="text-center">
                        <div className="bg-white rounded-xl p-4 shadow-md border border-slate-200">
                          <div className="text-3xl font-bold text-blue-600 mb-1">{selectedQuiz.totalMarks}</div>
                          <div className="text-sm text-gray-600 font-medium">Total Marks</div>
                        </div>
                      </div>
                      <div className="text-center">
                        <div className="bg-white rounded-xl p-4 shadow-md border border-slate-200">
                          <div className="text-3xl font-bold text-purple-600 mb-1">{selectedQuiz.durationMinutes}</div>
                          <div className="text-sm text-gray-600 font-medium">Minutes</div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Enhanced Questions List */}
                <div className="bg-white rounded-2xl shadow-xl border border-slate-200 overflow-hidden">
                  <div className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-6 py-4">
                    <h3 className="text-xl font-bold flex items-center">
                      <div className="bg-white/20 backdrop-blur-sm rounded-lg p-2 mr-3">
                        <i className="fas fa-question-circle"></i>
                      </div>
                      Question Bank
                    </h3>
                  </div>
                  <div className="p-6">
                    {questions.length === 0 ? (
                      <div className="text-center py-16">
                        <div className="bg-gradient-to-br from-purple-100 to-pink-100 rounded-2xl p-8 max-w-md mx-auto">
                          <div className="bg-gradient-to-r from-purple-500 to-pink-500 rounded-full w-20 h-20 mx-auto mb-6 flex items-center justify-center">
                            <i className="fas fa-question-circle text-white text-3xl"></i>
                          </div>
                          <h4 className="text-xl font-bold text-gray-800 mb-2">No questions added yet</h4>
                          <p className="text-gray-600 mb-6">Start building your quiz by adding your first question</p>
                          <button
                            onClick={() => setShowQuestionForm(true)}
                            className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-6 py-3 rounded-xl hover:from-purple-600 hover:to-pink-600 transition-all duration-300 flex items-center mx-auto shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                          >
                            <i className="fas fa-plus mr-2"></i>
                            Add Your First Question
                          </button>
                        </div>
                      </div>
                    ) : (
                      <div className="space-y-6">
                        {questions.map((question, index) => (
                          <div key={question.id} className="bg-gradient-to-r from-slate-50 to-blue-50 border border-slate-200 rounded-2xl overflow-hidden hover:shadow-lg transition-all duration-300 transform hover:-translate-y-1">
                            <div className="p-6">
                              <div className="flex justify-between items-start mb-4">
                                <div className="flex items-start space-x-4 flex-1">
                                  <div className="bg-gradient-to-r from-blue-500 to-purple-500 text-white rounded-xl w-12 h-12 flex items-center justify-center font-bold text-lg shadow-lg">
                                    {index + 1}
                                  </div>
                                  <div className="flex-1">
                                    <h4 className="font-bold text-gray-800 mb-4 text-lg leading-relaxed">
                                      {question.questionText}
                                    </h4>
                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                                      {['A', 'B', 'C', 'D'].map((option) => (
                                        <div
                                          key={option}
                                          className={`p-4 rounded-xl border-2 transition-all duration-300 ${
                                            question.correctAnswer === option
                                              ? 'bg-gradient-to-r from-green-50 to-emerald-50 border-green-300 shadow-md'
                                              : 'bg-white border-slate-200 hover:border-slate-300'
                                          }`}
                                        >
                                          <div className="flex items-center space-x-3">
                                            <div className={`w-8 h-8 rounded-full flex items-center justify-center font-bold text-sm ${
                                              question.correctAnswer === option
                                                ? 'bg-green-500 text-white'
                                                : 'bg-slate-200 text-slate-600'
                                            }`}>
                                              {option}
                                            </div>
                                            <span className="flex-1 text-gray-700">{question[`option${option}`]}</span>
                                            {question.correctAnswer === option && (
                                              <div className="bg-green-500 rounded-full w-6 h-6 flex items-center justify-center">
                                                <i className="fas fa-check text-white text-xs"></i>
                                              </div>
                                            )}
                                          </div>
                                        </div>
                                      ))}
                                    </div>
                                  </div>
                                </div>
                                <div className="flex items-center space-x-3 ml-4">
                                  <div className="bg-gradient-to-r from-yellow-400 to-orange-400 text-white px-4 py-2 rounded-xl font-medium shadow-md">
                                    <i className="fas fa-star mr-1"></i>
                                    {question.marks} mark{question.marks !== 1 ? 's' : ''}
                                  </div>
                                  <button
                                    onClick={() => {
                                      setEditingQuestion(question);
                                      setShowQuestionForm(true);
                                    }}
                                    className="bg-blue-500 hover:bg-blue-600 text-white rounded-xl w-10 h-10 flex items-center justify-center transition-all duration-300 shadow-md hover:shadow-lg transform hover:-translate-y-1"
                                  >
                                    <i className="fas fa-edit"></i>
                                  </button>
                                  <button
                                    onClick={() => handleDeleteQuestion(question.id)}
                                    className="bg-red-500 hover:bg-red-600 text-white rounded-xl w-10 h-10 flex items-center justify-center transition-all duration-300 shadow-md hover:shadow-lg transform hover:-translate-y-1"
                                  >
                                    <i className="fas fa-trash"></i>
                                  </button>
                                </div>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ) : (
              <div className="bg-white rounded-2xl shadow-xl border border-slate-200 overflow-hidden">
                <div className="p-16 text-center">
                  <div className="bg-gradient-to-br from-slate-100 to-blue-100 rounded-2xl p-8 max-w-md mx-auto">
                    <div className="bg-gradient-to-r from-slate-400 to-blue-400 rounded-full w-20 h-20 mx-auto mb-6 flex items-center justify-center">
                      <i className="fas fa-arrow-left text-white text-3xl"></i>
                    </div>
                    <h4 className="text-xl font-bold text-gray-800 mb-2">Select a quiz to manage questions</h4>
                    <p className="text-gray-600">Choose a quiz from the left panel to view and edit its questions</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Quiz Creation Modal */}
        {showQuizForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-2xl max-w-2xl w-full max-h-screen overflow-y-auto shadow-2xl">
              <QuizCreationForm
                onQuizCreated={(updatedQuiz) => {
                  if (selectedQuiz) {
                    // Update existing quiz
                    setQuizzes(quizzes.map(q => q.id === selectedQuiz.id ? updatedQuiz : q));
                    setSelectedQuiz(null);
                  } else {
                    // Add new quiz
                    setQuizzes([...quizzes, updatedQuiz]);
                  }
                  setShowQuizForm(false);
                  setQuizFormData({ title: '', description: '', durationMinutes: 30 });
                }}
                onClose={() => {
                  setShowQuizForm(false);
                  setSelectedQuiz(null);
                  setQuizFormData({ title: '', description: '', durationMinutes: 30 });
                }}
                editingQuiz={selectedQuiz && showQuizForm ? selectedQuiz : null}
              />
            </div>
          </div>
        )}

        {/* Enhanced Question Form Modal */}
        {showQuestionForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-2xl max-w-5xl w-full max-h-screen overflow-y-auto shadow-2xl">
              <QuestionForm
                quizId={selectedQuiz.id}
                existingQuestion={editingQuestion}
                onQuestionAdded={handleQuestionAdded}
                onClose={() => {
                  setShowQuestionForm(false);
                  setEditingQuestion(null);
                }}
              />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default QuizManagement;
