import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { quizAPI, leaderboardAPI } from '../services/api';

const Dashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalQuizzes: 0,
    recentAttempts: [],
  });
  const [availableQuizzes, setAvailableQuizzes] = useState([]);
  const [topPerformers, setTopPerformers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [quizzesResponse, historyResponse] = await Promise.all([
        quizAPI.getAvailableQuizzes(),
        quizAPI.getUserHistory(),
      ]);

      setAvailableQuizzes(quizzesResponse.data);
      setStats({
        totalQuizzes: quizzesResponse.data.length,
        recentAttempts: historyResponse.data.slice(0, 5),
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getScoreColor = (score, totalQuestions) => {
    const percentage = (score / (totalQuestions * 1)) * 100; // Assuming 1 mark per question
    if (percentage >= 80) return 'text-success';
    if (percentage >= 60) return 'text-warning';
    return 'text-danger';
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '80vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {/* Welcome Section */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="card bg-primary text-white">
            <div className="card-body">
              <div className="d-flex align-items-center">
                <div className="me-3">
                  <i className="fas fa-user-circle fa-3x"></i>
                </div>
                <div>
                  <h2 className="card-title mb-1">Welcome back, {user?.username}!</h2>
                  <p className="card-text mb-0">
                    Ready to challenge yourself with some quizzes?
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="row mb-4">
        <div className="col-md-3">
          <div className="card text-center bg-light">
            <div className="card-body">
              <i className="fas fa-list-alt fa-2x text-primary mb-2"></i>
              <h4 className="card-title text-primary">{stats.totalQuizzes}</h4>
              <p className="card-text">Available Quizzes</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-center bg-light">
            <div className="card-body">
              <i className="fas fa-history fa-2x text-success mb-2"></i>
              <h4 className="card-title text-success">{stats.recentAttempts.length}</h4>
              <p className="card-text">Recent Attempts</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-center bg-light">
            <div className="card-body">
              <i className="fas fa-trophy fa-2x text-warning mb-2"></i>
              <h4 className="card-title text-warning">
                {stats.recentAttempts.length > 0 
                  ? Math.max(...stats.recentAttempts.map(a => a.score))
                  : 0}
              </h4>
              <p className="card-text">Best Score</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-center bg-light">
            <div className="card-body">
              <i className="fas fa-percentage fa-2x text-info mb-2"></i>
              <h4 className="card-title text-info">
                {stats.recentAttempts.length > 0
                  ? Math.round(
                      stats.recentAttempts.reduce((acc, curr) => 
                        acc + (curr.correctAnswers / curr.totalQuestions) * 100, 0
                      ) / stats.recentAttempts.length
                    )
                  : 0}%
              </h4>
              <p className="card-text">Average Score</p>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        {/* Available Quizzes */}
        <div className="col-md-8">
          <div className="card">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h5 className="mb-0">
                <i className="fas fa-list-alt me-2"></i>Available Quizzes
              </h5>
              <Link to="/quizzes" className="btn btn-sm btn-outline-primary">
                View All
              </Link>
            </div>
            <div className="card-body">
              {availableQuizzes.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-inbox fa-3x text-muted mb-3"></i>
                  <p className="text-muted">No quizzes available at the moment.</p>
                </div>
              ) : (
                <div className="row">
                  {availableQuizzes.slice(0, 4).map((quiz) => (
                    <div key={quiz.id} className="col-md-6 mb-3">
                      <div className="card border">
                        <div className="card-body">
                          <h6 className="card-title">{quiz.title}</h6>
                          <p className="card-text small text-muted">
                            {quiz.description || 'No description available'}
                          </p>
                          <div className="d-flex justify-content-between align-items-center">
                            <small className="text-muted">
                              <i className="fas fa-clock me-1"></i>
                              {quiz.durationMinutes} mins
                            </small>
                            <Link
                              to={`/quiz/${quiz.id}`}
                              className="btn btn-sm btn-primary"
                            >
                              Start
                            </Link>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Recent Activity */}
        <div className="col-md-4">
          <div className="card">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h5 className="mb-0">
                <i className="fas fa-history me-2"></i>Recent Activity
              </h5>
              <Link to="/history" className="btn btn-sm btn-outline-secondary">
                View All
              </Link>
            </div>
            <div className="card-body">
              {stats.recentAttempts.length === 0 ? (
                <div className="text-center py-4">
                  <i className="fas fa-clock fa-2x text-muted mb-2"></i>
                  <p className="text-muted small">No quiz attempts yet.</p>
                </div>
              ) : (
                <div className="list-group list-group-flush">
                  {stats.recentAttempts.map((attempt) => (
                    <div key={attempt.id} className="list-group-item px-0 py-2">
                      <div className="d-flex justify-content-between align-items-start">
                        <div className="flex-grow-1">
                          <h6 className="mb-1 small">{attempt.quizTitle}</h6>
                          <p className="mb-1 small text-muted">
                            Score: 
                            <span className={`ms-1 fw-bold ${getScoreColor(attempt.score, attempt.totalQuestions)}`}>
                              {attempt.score}/{attempt.totalQuestions}
                            </span>
                          </p>
                          <small className="text-muted">
                            {formatDate(attempt.completedAt)}
                          </small>
                        </div>
                        <Link
                          to={`/result/${attempt.id}`}
                          className="btn btn-sm btn-outline-secondary"
                        >
                          View
                        </Link>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="row mt-4">
        <div className="col-12">
          <div className="card">
            <div className="card-body text-center">
              <h5 className="card-title">Quick Actions</h5>
              <div className="btn-group" role="group">
                <Link to="/quizzes" className="btn btn-primary">
                  <i className="fas fa-play me-2"></i>Take a Quiz
                </Link>
                <Link to="/history" className="btn btn-outline-secondary">
                  <i className="fas fa-history me-2"></i>View History
                </Link>
                <Link to="/leaderboard" className="btn btn-outline-warning">
                  <i className="fas fa-trophy me-2"></i>Leaderboard
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
