import React, { useState, useEffect } from 'react';
import { adminAPI } from '../services/api';

const QuestionForm = ({ quizId, onQuestionAdded, onClose, existingQuestion = null }) => {
  const [formData, setFormData] = useState({
    questionText: '',
    optionA: '',
    optionB: '',
    optionC: '',
    optionD: '',
    correctAnswer: '',
    marks: 1,
    quizId: quizId
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (existingQuestion) {
      setFormData({
        questionText: existingQuestion.questionText || '',
        optionA: existingQuestion.optionA || '',
        optionB: existingQuestion.optionB || '',
        optionC: existingQuestion.optionC || '',
        optionD: existingQuestion.optionD || '',
        correctAnswer: existingQuestion.correctAnswer || '',
        marks: existingQuestion.marks || 1,
        quizId: quizId
      });
    }
  }, [existingQuestion, quizId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.questionText.trim()) {
      newErrors.questionText = 'Question text is required';
    }
    
    if (!formData.optionA.trim()) {
      newErrors.optionA = 'Option A is required';
    }
    
    if (!formData.optionB.trim()) {
      newErrors.optionB = 'Option B is required';
    }
    
    if (!formData.optionC.trim()) {
      newErrors.optionC = 'Option C is required';
    }
    
    if (!formData.optionD.trim()) {
      newErrors.optionD = 'Option D is required';
    }
    
    if (!formData.correctAnswer) {
      newErrors.correctAnswer = 'Please select the correct answer';
    }
    
    if (!formData.marks || formData.marks < 1) {
      newErrors.marks = 'Marks must be at least 1';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    try {
      setLoading(true);
      
      if (existingQuestion) {
        await adminAPI.updateQuestion(existingQuestion.id, formData);
      } else {
        await adminAPI.createQuestion(formData);
      }
      
      // Reset form
      setFormData({
        questionText: '',
        optionA: '',
        optionB: '',
        optionC: '',
        optionD: '',
        correctAnswer: '',
        marks: 1,
        quizId: quizId
      });
      
      if (onQuestionAdded) {
        onQuestionAdded();
      }
      
      if (onClose) {
        onClose();
      }
      
    } catch (error) {
      console.error('Error saving question:', error);
      setErrors({ submit: 'Error saving question. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white rounded-2xl shadow-2xl overflow-hidden">
      {/* Enhanced Header */}
      <div className="bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 text-white px-8 py-6">
        <div className="flex items-center">
          <div className="bg-white/20 backdrop-blur-sm rounded-xl p-3 mr-4">
            <i className="fas fa-question-circle text-2xl"></i>
          </div>
          <div>
            <h2 className="text-2xl font-bold">
              {existingQuestion ? 'Edit Question' : 'Create New Question'}
            </h2>
            <p className="text-indigo-100 mt-1">
              {existingQuestion ? 'Modify the question details below' : 'Add a new question to your quiz'}
            </p>
          </div>
        </div>
      </div>
      
      <div className="p-8 bg-gradient-to-br from-slate-50 to-blue-50">
        <form onSubmit={handleSubmit} className="space-y-8">
          {/* Enhanced Question Text */}
          <div className="bg-white rounded-xl p-6 shadow-md border border-slate-200">
            <label className="flex items-center text-lg font-bold text-gray-800 mb-4">
              <div className="bg-blue-500 rounded-lg p-2 mr-3">
                <i className="fas fa-question text-white"></i>
              </div>
              Question Text
            </label>
            <textarea
              name="questionText"
              value={formData.questionText}
              onChange={handleChange}
              className={`w-full px-4 py-4 text-lg border-2 rounded-xl focus:outline-none focus:ring-4 focus:ring-blue-500/20 transition-all duration-300 resize-none ${
                errors.questionText 
                  ? 'border-red-400 bg-red-50' 
                  : 'border-slate-300 hover:border-blue-400 focus:border-blue-500'
              }`}
              rows="4"
              placeholder="Type your question here... Make it clear and engaging!"
            />
            {errors.questionText && (
              <div className="flex items-center mt-3 text-red-600 bg-red-50 px-4 py-2 rounded-lg">
                <i className="fas fa-exclamation-circle mr-2"></i>
                <span className="font-medium">{errors.questionText}</span>
              </div>
            )}
          </div>

          {/* Enhanced Answer Options */}
          <div className="bg-white rounded-xl p-6 shadow-md border border-slate-200">
            <label className="flex items-center text-lg font-bold text-gray-800 mb-6">
              <div className="bg-green-500 rounded-lg p-2 mr-3">
                <i className="fas fa-list text-white"></i>
              </div>
              Answer Options
              <span className="ml-3 text-sm font-normal text-gray-500 bg-gray-100 px-3 py-1 rounded-full">
                Click an option to mark it as correct
              </span>
            </label>
            
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Option A */}
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <label className={`flex items-center text-lg font-semibold cursor-pointer transition-all duration-300 ${
                    formData.correctAnswer === 'A' 
                      ? 'text-green-700' 
                      : 'text-gray-700 hover:text-green-600'
                  }`}>
                    <input
                      type="radio"
                      name="correctAnswer"
                      value="A"
                      checked={formData.correctAnswer === 'A'}
                      onChange={handleChange}
                      className="hidden"
                    />
                    <div className={`w-10 h-10 rounded-xl flex items-center justify-center font-bold text-lg mr-3 transition-all duration-300 ${
                      formData.correctAnswer === 'A'
                        ? 'bg-gradient-to-r from-green-500 to-emerald-500 text-white shadow-lg'
                        : 'bg-gray-200 text-gray-600 hover:bg-green-100'
                    }`}>
                      A
                    </div>
                    Option A
                  </label>
                  {formData.correctAnswer === 'A' && (
                    <div className="bg-green-500 rounded-full w-8 h-8 flex items-center justify-center">
                      <i className="fas fa-check text-white text-sm"></i>
                    </div>
                  )}
                </div>
                <input
                  type="text"
                  name="optionA"
                  value={formData.optionA}
                  onChange={handleChange}
                  className={`w-full px-4 py-3 text-lg border-2 rounded-xl focus:outline-none focus:ring-4 focus:ring-green-500/20 transition-all duration-300 cursor-pointer ${
                    formData.correctAnswer === 'A'
                      ? 'border-green-400 bg-green-50 shadow-md'
                      : errors.optionA
                      ? 'border-red-400 bg-red-50'
                      : 'border-slate-300 hover:border-green-400 focus:border-green-500'
                  }`}
                  placeholder="Enter option A"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'A' }))}
                />
                {errors.optionA && (
                  <div className="flex items-center text-red-600 bg-red-50 px-3 py-2 rounded-lg">
                    <i className="fas fa-exclamation-circle mr-2 text-sm"></i>
                    <span className="text-sm">{errors.optionA}</span>
                  </div>
                )}
              </div>

              {/* Option B */}
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <label className={`flex items-center text-lg font-semibold cursor-pointer transition-all duration-300 ${
                    formData.correctAnswer === 'B' 
                      ? 'text-green-700' 
                      : 'text-gray-700 hover:text-green-600'
                  }`}>
                    <input
                      type="radio"
                      name="correctAnswer"
                      value="B"
                      checked={formData.correctAnswer === 'B'}
                      onChange={handleChange}
                      className="hidden"
                    />
                    <div className={`w-10 h-10 rounded-xl flex items-center justify-center font-bold text-lg mr-3 transition-all duration-300 ${
                      formData.correctAnswer === 'B'
                        ? 'bg-gradient-to-r from-green-500 to-emerald-500 text-white shadow-lg'
                        : 'bg-gray-200 text-gray-600 hover:bg-green-100'
                    }`}>
                      B
                    </div>
                    Option B
                  </label>
                  {formData.correctAnswer === 'B' && (
                    <div className="bg-green-500 rounded-full w-8 h-8 flex items-center justify-center">
                      <i className="fas fa-check text-white text-sm"></i>
                    </div>
                  )}
                </div>
                <input
                  type="text"
                  name="optionB"
                  value={formData.optionB}
                  onChange={handleChange}
                  className={`w-full px-4 py-3 text-lg border-2 rounded-xl focus:outline-none focus:ring-4 focus:ring-green-500/20 transition-all duration-300 cursor-pointer ${
                    formData.correctAnswer === 'B'
                      ? 'border-green-400 bg-green-50 shadow-md'
                      : errors.optionB
                      ? 'border-red-400 bg-red-50'
                      : 'border-slate-300 hover:border-green-400 focus:border-green-500'
                  }`}
                  placeholder="Enter option B"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'B' }))}
                />
                {errors.optionB && (
                  <div className="flex items-center text-red-600 bg-red-50 px-3 py-2 rounded-lg">
                    <i className="fas fa-exclamation-circle mr-2 text-sm"></i>
                    <span className="text-sm">{errors.optionB}</span>
                  </div>
                )}
              </div>

              {/* Option C */}
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <label className={`flex items-center text-lg font-semibold cursor-pointer transition-all duration-300 ${
                    formData.correctAnswer === 'C' 
                      ? 'text-green-700' 
                      : 'text-gray-700 hover:text-green-600'
                  }`}>
                    <input
                      type="radio"
                      name="correctAnswer"
                      value="C"
                      checked={formData.correctAnswer === 'C'}
                      onChange={handleChange}
                      className="hidden"
                    />
                    <div className={`w-10 h-10 rounded-xl flex items-center justify-center font-bold text-lg mr-3 transition-all duration-300 ${
                      formData.correctAnswer === 'C'
                        ? 'bg-gradient-to-r from-green-500 to-emerald-500 text-white shadow-lg'
                        : 'bg-gray-200 text-gray-600 hover:bg-green-100'
                    }`}>
                      C
                    </div>
                    Option C
                  </label>
                  {formData.correctAnswer === 'C' && (
                    <div className="bg-green-500 rounded-full w-8 h-8 flex items-center justify-center">
                      <i className="fas fa-check text-white text-sm"></i>
                    </div>
                  )}
                </div>
                <input
                  type="text"
                  name="optionC"
                  value={formData.optionC}
                  onChange={handleChange}
                  className={`w-full px-4 py-3 text-lg border-2 rounded-xl focus:outline-none focus:ring-4 focus:ring-green-500/20 transition-all duration-300 cursor-pointer ${
                    formData.correctAnswer === 'C'
                      ? 'border-green-400 bg-green-50 shadow-md'
                      : errors.optionC
                      ? 'border-red-400 bg-red-50'
                      : 'border-slate-300 hover:border-green-400 focus:border-green-500'
                  }`}
                  placeholder="Enter option C"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'C' }))}
                />
                {errors.optionC && (
                  <div className="flex items-center text-red-600 bg-red-50 px-3 py-2 rounded-lg">
                    <i className="fas fa-exclamation-circle mr-2 text-sm"></i>
                    <span className="text-sm">{errors.optionC}</span>
                  </div>
                )}
              </div>

              {/* Option D */}
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <label className={`flex items-center text-lg font-semibold cursor-pointer transition-all duration-300 ${
                    formData.correctAnswer === 'D' 
                      ? 'text-green-700' 
                      : 'text-gray-700 hover:text-green-600'
                  }`}>
                    <input
                      type="radio"
                      name="correctAnswer"
                      value="D"
                      checked={formData.correctAnswer === 'D'}
                      onChange={handleChange}
                      className="hidden"
                    />
                    <div className={`w-10 h-10 rounded-xl flex items-center justify-center font-bold text-lg mr-3 transition-all duration-300 ${
                      formData.correctAnswer === 'D'
                        ? 'bg-gradient-to-r from-green-500 to-emerald-500 text-white shadow-lg'
                        : 'bg-gray-200 text-gray-600 hover:bg-green-100'
                    }`}>
                      D
                    </div>
                    Option D
                  </label>
                  {formData.correctAnswer === 'D' && (
                    <div className="bg-green-500 rounded-full w-8 h-8 flex items-center justify-center">
                      <i className="fas fa-check text-white text-sm"></i>
                    </div>
                  )}
                </div>
                <input
                  type="text"
                  name="optionD"
                  value={formData.optionD}
                  onChange={handleChange}
                  className={`w-full px-4 py-3 text-lg border-2 rounded-xl focus:outline-none focus:ring-4 focus:ring-green-500/20 transition-all duration-300 cursor-pointer ${
                    formData.correctAnswer === 'D'
                      ? 'border-green-400 bg-green-50 shadow-md'
                      : errors.optionD
                      ? 'border-red-400 bg-red-50'
                      : 'border-slate-300 hover:border-green-400 focus:border-green-500'
                  }`}
                  placeholder="Enter option D"
                  onClick={() => setFormData(prev => ({ ...prev, correctAnswer: 'D' }))}
                />
                {errors.optionD && (
                  <div className="flex items-center text-red-600 bg-red-50 px-3 py-2 rounded-lg">
                    <i className="fas fa-exclamation-circle mr-2 text-sm"></i>
                    <span className="text-sm">{errors.optionD}</span>
                  </div>
                )}
              </div>
            </div>
            
            {errors.correctAnswer && (
              <div className="flex items-center mt-4 text-red-600 bg-red-50 px-4 py-3 rounded-lg">
                <i className="fas fa-exclamation-circle mr-2"></i>
                <span className="font-medium">{errors.correctAnswer}</span>
              </div>
            )}
          </div>

          {/* Enhanced Marks Section */}
          <div className="bg-white rounded-xl p-6 shadow-md border border-slate-200">
            <label className="flex items-center text-lg font-bold text-gray-800 mb-4">
              <div className="bg-yellow-500 rounded-lg p-2 mr-3">
                <i className="fas fa-star text-white"></i>
              </div>
              Question Points
            </label>
            <div className="flex items-center space-x-4">
              <input
                type="number"
                name="marks"
                value={formData.marks}
                onChange={handleChange}
                className={`px-4 py-3 text-lg font-bold border-2 rounded-xl focus:outline-none focus:ring-4 focus:ring-yellow-500/20 transition-all duration-300 w-32 text-center ${
                  errors.marks 
                    ? 'border-red-400 bg-red-50' 
                    : 'border-slate-300 hover:border-yellow-400 focus:border-yellow-500'
                }`}
                min="1"
                max="10"
              />
              <span className="text-gray-600 font-medium">points for this question</span>
            </div>
            {errors.marks && (
              <div className="flex items-center mt-3 text-red-600 bg-red-50 px-4 py-2 rounded-lg">
                <i className="fas fa-exclamation-circle mr-2"></i>
                <span className="font-medium">{errors.marks}</span>
              </div>
            )}
          </div>

          {/* Enhanced Correct Answer Preview */}
          {formData.correctAnswer && formData[`option${formData.correctAnswer}`] && (
            <div className="bg-gradient-to-r from-green-50 to-emerald-50 border-2 border-green-200 rounded-xl p-6 shadow-md">
              <div className="flex items-center mb-3">
                <div className="bg-green-500 rounded-full w-10 h-10 flex items-center justify-center mr-3">
                  <i className="fas fa-check text-white"></i>
                </div>
                <h4 className="text-lg font-bold text-green-800">Correct Answer Preview</h4>
              </div>
              <div className="bg-white rounded-lg p-4 border border-green-200">
                <div className="flex items-center space-x-3">
                  <div className="bg-green-500 rounded-lg w-8 h-8 flex items-center justify-center font-bold text-white">
                    {formData.correctAnswer}
                  </div>
                  <span className="text-green-800 font-medium text-lg">
                    {formData[`option${formData.correctAnswer}`]}
                  </span>
                </div>
              </div>
            </div>
          )}

          {/* Enhanced Error Message */}
          {errors.submit && (
            <div className="bg-red-50 border-l-4 border-red-400 p-4 rounded-r-xl">
              <div className="flex items-center">
                <div className="bg-red-100 rounded-full p-2 mr-3">
                  <i className="fas fa-exclamation-triangle text-red-500"></i>
                </div>
                <div>
                  <h4 className="text-red-800 font-medium">Error</h4>
                  <p className="text-red-700">{errors.submit}</p>
                </div>
              </div>
            </div>
          )}

          {/* Enhanced Action Buttons */}
          <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
            <button
              type="button"
              onClick={onClose}
              disabled={loading}
              className="px-8 py-3 text-lg font-medium text-gray-700 bg-white border-2 border-gray-300 rounded-xl hover:bg-gray-50 hover:border-gray-400 focus:outline-none focus:ring-4 focus:ring-gray-500/20 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <i className="fas fa-times mr-2"></i>
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              className="px-8 py-3 text-lg font-medium text-white bg-gradient-to-r from-indigo-600 to-purple-600 rounded-xl hover:from-indigo-700 hover:to-purple-700 focus:outline-none focus:ring-4 focus:ring-indigo-500/50 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
            >
              {loading ? (
                <>
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white inline-block mr-2"></div>
                  Saving...
                </>
              ) : (
                <>
                  <i className="fas fa-save mr-2"></i>
                  {existingQuestion ? 'Update Question' : 'Add Question'}
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default QuestionForm;
